import { useState, useMemo } from "react";
import { MainLayout } from "@/components/layout/main-layout";
import { Input } from "@/components/ui/input";
import { RefreshCw, Loader2 } from "lucide-react";
import { useWebSocketContext } from "@/hooks/use-websocket-context";
import { useToast } from "@/hooks/use-toast";
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue
} from "@/components/ui/select";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { LicenseRequest, LicenseStatus } from "@shared/schema";
import { format, isAfter, isBefore, addDays, differenceInDays } from "date-fns";
import { Button } from "@/components/ui/button";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { 
  Pagination, 
  PaginationContent, 
  PaginationItem, 
  PaginationLink, 
  PaginationNext, 
  PaginationPrevious 
} from "@/components/ui/pagination";
import { 
  Dialog, 
  DialogContent, 
  DialogTitle, 
  DialogHeader, 
  DialogDescription,
  DialogFooter,
  DialogClose
} from "@/components/ui/dialog";
import { FileDown, ExternalLink, AlertCircle, CheckCircle2, Clock, RefreshCcw, Download } from "lucide-react";
import { formatDateForCSV, exportToCSV } from "@/lib/csv-export";
import { Status, StatusBadge } from "@/components/licenses/status-badge";
import { TransporterInfo } from "@/components/transporters/transporter-info";
import { Badge } from "@/components/ui/badge";
import { SortableHeader } from "@/components/ui/sortable-header";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useLocation } from "wouter";
import { usePaginatedList } from "@/hooks/use-paginated-list";
import { ListPagination, MobileListPagination } from "@/components/ui/list-pagination";

// Extensão do tipo LicenseRequest para incluir dados do transportador
interface LicenseRequestWithTransporter extends LicenseRequest {
  transporter?: {
    id: number;
    name: string;
    tradeName: string;
    documentNumber: string;
  } | null;
}

// Tipo para as licenças expandidas (uma linha por estado)
interface ExpandedLicenseRequest {
  id: number;
  licenseId: number;
  requestNumber: string;
  type: string;
  mainVehiclePlate: string;
  state: string;
  status: string;
  stateStatus: string;
  emissionDate: string | null;
  validUntil: string | null;
  licenseFileUrl: string | null;
  stateFileUrl: string | null;
  transporterId: number;
  transporter?: {
    id: number;
    name: string;
    tradeName: string;
    documentNumber: string;
  } | null;
  aetNumber: string | null;
}

export default function IssuedLicensesPage() {
  const [searchTerm, setSearchTerm] = useState("");
  const [dateFrom, setDateFrom] = useState("");
  const [dateTo, setDateTo] = useState("");
  const [stateFilter, setStateFilter] = useState("");
  const [statusFilter, setStatusFilter] = useState("");

  const [selectedLicense, setSelectedLicense] = useState<LicenseRequestWithTransporter | null>(null);
  const [sortColumn, setSortColumn] = useState<string | null>("emissionDate");
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc' | null>('desc');
  const [renewDialogOpen, setRenewDialogOpen] = useState(false);
  const [licenseToRenew, setLicenseToRenew] = useState<{licenseId: number, state: string} | null>(null);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const queryClient = useQueryClient();
  const { isConnected } = useWebSocketContext();
  const { toast } = useToast();

  const { data: issuedLicenses, isLoading, refetch } = useQuery<LicenseRequestWithTransporter[]>({
    queryKey: ["/api/licenses/issued"],
    queryFn: async () => {
      const res = await fetch("/api/licenses/issued", {
        credentials: "include"
      });
      if (!res.ok) {
        throw new Error("Erro ao buscar licenças emitidas");
      }
      return res.json();
    },
    // Desabilita o cache para garantir que sempre temos os dados mais recentes
    staleTime: 0,
    // Recarrega os dados quando a página recebe foco
    refetchOnWindowFocus: true
  });

  // Interface para as licenças expandidas por estado

  // Obter licenças com status aprovado por estado
  const expandedLicenses = useMemo(() => {
    if (!issuedLicenses) return [];

    const result: ExpandedLicenseRequest[] = [];

    issuedLicenses.forEach(license => {
      // Para cada licença, expandir para uma linha por estado que tenha sido aprovado
      license.states.forEach((state, index) => {
        // Verifica se este estado específico foi aprovado
        const stateStatusEntry = license.stateStatuses?.find(entry => entry.startsWith(`${state}:`));
        const stateStatus = stateStatusEntry?.split(':')?.[1] || 'pending_registration';
        const stateFileEntry = license.stateFiles?.find(entry => entry.startsWith(`${state}:`));
        const stateFileUrl = stateFileEntry?.split(':')?.[1] || null;

        // Só incluir estados com status "approved"
        if (stateStatus === 'approved') {
          // Obter data de validade e emissão específicas para este estado, se disponível
          let stateValidUntil = null;
          let stateEmissionDate = null;

          // Novo formato: "estado:status:data_validade" ou "estado:status:data_validade:data_emissao"
          if (stateStatusEntry && stateStatusEntry.split(':').length > 2) {
            const statusParts = stateStatusEntry.split(':');
            // Extrair data de validade do formato estado:status:data
            stateValidUntil = statusParts[2];
            console.log(`Data de validade extraída para ${state}: ${stateValidUntil}`);

            // Se tiver 4 partes, pode incluir data de emissão
            if (statusParts.length > 3) {
              // A última parte poderia ser data de emissão se estiver no formato de data
              const lastPart = statusParts[statusParts.length - 1];
              if (lastPart.match(/^\d{4}-\d{2}-\d{2}$/)) {
                stateEmissionDate = lastPart;
                console.log(`Data de emissão extraída para ${state}: ${stateEmissionDate}`);
                console.log(`Status completo para ${state}: ${stateStatusEntry}`);
              }
            }
          }

          // Se não encontrou data de emissão específica, usar a data global da licença
          if (!stateEmissionDate && license.issuedAt) {
            const issuedDate = typeof license.issuedAt === 'string' ? license.issuedAt : license.issuedAt.toISOString();
            stateEmissionDate = issuedDate.split('T')[0];
            console.log(`[DEBUG] Usando data global para ${state}: ${stateEmissionDate}`);
          }

          // Obter número AET específico para este estado, se disponível
          let stateAETNumber = null;

          // Verificar primeiro no array stateAETNumbers (formato "SP:123456")
          if (license.stateAETNumbers && Array.isArray(license.stateAETNumbers)) {
            const aetEntry = license.stateAETNumbers.find(entry => entry.startsWith(`${state}:`));
            if (aetEntry) {
              const parts = aetEntry.split(':');
              if (parts.length >= 2) {
                stateAETNumber = parts[1];
              }
            }
          }

          // Se não encontrou no stateAETNumbers, tentar no campo aetNumber (legado)
          if (!stateAETNumber && license.aetNumber) {
            stateAETNumber = license.aetNumber;
          }

          // Se não encontrou data de validade específica, usar a data global da licença
          if (!stateValidUntil && license.validUntil) {
            const validDate = typeof license.validUntil === 'string' ? license.validUntil : license.validUntil.toISOString();
            stateValidUntil = validDate.split('T')[0];
            console.log(`[DEBUG] Usando data validade global para ${state}: ${stateValidUntil}`);
          }



          result.push({
            id: license.id * 100 + index, // Gerar ID único para a linha
            licenseId: license.id,
            requestNumber: license.requestNumber,
            type: license.type,
            mainVehiclePlate: license.mainVehiclePlate,
            state,
            status: stateStatus,
            stateStatus,
            emissionDate: stateEmissionDate,
            validUntil: stateValidUntil,
            licenseFileUrl: license.licenseFileUrl,
            stateFileUrl,
            transporterId: license.transporterId || 0,
            transporter: license.transporter, // Passar dados do transportador para a linha expandida
            aetNumber: stateAETNumber // Usar o número AET específico do estado
          });
        }
      });
    });

    return result;
  }, [issuedLicenses]);



  // Verificar validade das licenças
  const getLicenseStatus = (validUntil: string | null): 'active' | 'expired' | 'expiring_soon' => {
    if (!validUntil) return 'active';

    const validDate = new Date(validUntil);
    const today = new Date();

    if (isBefore(validDate, today)) {
      return 'expired';
    }

    // Se a validade é menos de 30 dias a partir de hoje
    if (differenceInDays(validDate, today) <= 30) {
      return 'expiring_soon';
    }

    return 'active';
  };

  // Filtrar as licenças expandidas
  const filteredLicenses = expandedLicenses.filter(license => {
    const matchesSearch = !searchTerm || 
      license.requestNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
      license.mainVehiclePlate.toLowerCase().includes(searchTerm.toLowerCase());

    const licenseDate = license.emissionDate ? new Date(license.emissionDate) : null;

    const matchesDateFrom = !dateFrom || (
      licenseDate && 
      licenseDate >= new Date(dateFrom)
    );

    const matchesDateTo = !dateTo || (
      licenseDate && 
      licenseDate <= new Date(dateTo)
    );

    const matchesState = !stateFilter || stateFilter === "all_states" || (
      license.state === stateFilter
    );

    // Verificar o status da licença para filtro de situação
    const validityStatus = getLicenseStatus(license.validUntil);
    const matchesStatus = !statusFilter || statusFilter === "all_status" || statusFilter === validityStatus;

    return matchesSearch && matchesDateFrom && matchesDateTo && matchesState && matchesStatus;
  });

  // Ordenar licenças filtradas
  const sortedLicenses = useMemo(() => {
    if (!sortColumn || !sortDirection) {
      return filteredLicenses;
    }

    // Criar uma cópia para ordenação
    const toSort = [...filteredLicenses];

    // Definir uma função de ordenação personalizada com base na coluna e direção
    const getSortValue = (license: ExpandedLicenseRequest, column: string): any => {
      if (column === 'state') {
        return license.state;
      } else if (column === 'mainVehiclePlate') {
        return license.mainVehiclePlate;
      } else if (column === 'type') {
        return license.type;
      } else if (column === 'requestNumber') {
        return license.requestNumber;
      } else if (column === 'validUntil') {
        return license.validUntil ? new Date(license.validUntil).getTime() : 0;
      } else if (column === 'emissionDate') {
        return license.emissionDate ? new Date(license.emissionDate).getTime() : 0;
      } else if (column === 'status') {
        return getLicenseStatus(license.validUntil);
      } else {
        return license[column as keyof typeof license];
      }
    };

    // Ordenar o array
    toSort.sort((a, b) => {
      const aValue = getSortValue(a, sortColumn);
      const bValue = getSortValue(b, sortColumn);

      // Valores iguais
      if (aValue === bValue) return 0;

      // Tratamento para nulos
      if (aValue === null || aValue === undefined) {
        return sortDirection === 'asc' ? 1 : -1;
      }
      if (bValue === null || bValue === undefined) {
        return sortDirection === 'asc' ? -1 : 1;
      }

      // Para strings
      if (typeof aValue === 'string' && typeof bValue === 'string') {
        return sortDirection === 'asc' 
          ? aValue.localeCompare(bValue) 
          : bValue.localeCompare(aValue);
      }

      // Para números e outros tipos
      return sortDirection === 'asc' 
        ? (aValue < bValue ? -1 : 1) 
        : (bValue < aValue ? -1 : 1);
    });

    return toSort;
  }, [filteredLicenses, sortColumn, sortDirection]);

  // Função para ordenar as licenças
  const handleSort = (column: string) => {
    if (sortColumn === column) {
      // Se já está ordenando por esta coluna, alterna a direção
      if (sortDirection === 'asc') {
        setSortDirection('desc');
      } else if (sortDirection === 'desc') {
        setSortDirection('asc');
      } else {
        setSortDirection('asc');
      }
    } else {
      // Nova coluna selecionada, começa com ascendente
      setSortColumn(column);
      setSortDirection('asc');
    }
  };

  // Hook de paginação das licenças filtradas
  const { 
    paginatedItems: paginatedLicenses, 
    pagination, 
    currentPage, 
    setCurrentPage 
  } = usePaginatedList({ items: sortedLicenses });


  const viewLicenseDetails = (license: LicenseRequest) => {
    setSelectedLicense(license);
  };

  // Navegação para redirecionar após renovação
  const [, setLocation] = useLocation();

  // Mutação para renovar licença
  const renewLicenseMutation = useMutation({
    mutationFn: async ({ licenseId, state }: { licenseId: number, state: string }) => {
      try {
        // Usar o endpoint que aceita o corpo da requisição
        const response = await apiRequest("POST", "/api/licenses/renew", { licenseId, state });

        if (!response.ok) {
          const errorText = await response.text();
          throw new Error(`Erro ao renovar licença: ${errorText}`);
        }
        return await response.json();
      } catch (error) {
        console.error("Erro na renovação:", error);
        throw error;
      }
    },
    onSuccess: (data) => {
      console.log("Renovação bem-sucedida:", data);

      // Fechar o diálogo de renovação
      setRenewDialogOpen(false);

      // Invalidar a cache para garantir que os dados são atualizados
      queryClient.invalidateQueries({ queryKey: ["/api/licenses/drafts"] });
      queryClient.invalidateQueries({ queryKey: ["/api/licenses/issued"] });

      // Notificar o usuário e redirecionar para a página de edição do rascunho
      toast({
        title: "Licença renovada com sucesso",
        description: `Licença renovada para o estado ${data.draft.states[0]}. Você será redirecionado para editar o rascunho.`,
        duration: 5000,
      });

      // Navegar para a página de edição do rascunho após um pequeno atraso
      setTimeout(() => {
        setLocation(`/request-license?draft=${data.draft.id}`);
      }, 1000);
    },
    onError: (error: Error) => {
      console.error("Erro na renovação:", error);

      toast({
        title: "Erro ao renovar licença",
        description: error.message || "Não foi possível completar a renovação da licença. Tente novamente.",
        variant: "destructive",
        duration: 5000,
      });
    }
  });

  // Função para atualizar os dados com feedback visual melhorado
  const handleRefresh = async () => {
    setIsRefreshing(true);
    try {
      // Invalidar cache primeiro
      await queryClient.invalidateQueries({ queryKey: ["/api/licenses/issued"] });
      await queryClient.invalidateQueries({ queryKey: ["/api/licenses"] });

      // Fazer refetch
      await refetch();

      toast({
        title: "Sucesso",
        description: "Lista de licenças emitidas atualizada com sucesso.",
      });
    } catch (error) {
      console.error("Erro ao atualizar:", error);
      toast({
        title: "Erro",
        description: "Erro ao atualizar a lista. Tente novamente.",
        variant: "destructive",
      });
    } finally {
      setIsRefreshing(false);
    }
  };

  // Função para formatar data para CSV
  const formatDateToCSV = (dateString: string | null): string => {
    if (!dateString) return '';
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) return '';
      return date.toLocaleDateString('pt-BR');
    } catch {
      return '';
    }
  };

  // Função para traduzir tipos de veículo para português
  const translateVehicleType = (type: string): string => {
    const vehicleTypeMap: Record<string, string> = {
      'truck': 'Caminhão',
      'bitrain_9_axles': 'Bitrem 9 Eixos',
      'bitrain_7_axles': 'Bitrem 7 Eixos',
      'rodotrain': 'Rodotrem',
      'truck_trailer': 'Caminhão + Reboque',
      'articulated_truck': 'Caminhão Articulado',
      'special_transport': 'Transporte Especial',
      'oversized_cargo': 'Carga Superdimensionada'
    };

    return vehicleTypeMap[type] || type;
  };

  // Função para traduzir status para português
  const translateStatus = (status: string): string => {
    const statusMap: Record<string, string> = {
      'pending_registration': 'Pendente de Registro',
      'registration_in_progress': 'Registro em Andamento',
      'pending_documentation': 'Pendente Documentação',
      'under_review': 'Em Análise',
      'pending_approval': 'Pendente de Aprovação',
      'approved': 'Aprovado',
      'rejected': 'Rejeitado',
      'canceled': 'Cancelado'
    };

    return statusMap[status] || status;
  };

  // Função para exportar CSV das licenças emitidas
  const handleExportCSV = () => {
    try {
      const headers = [
        "Nº Solicitação",
        "Tipo de Veículo", 
        "Placa Principal",
        "Estado",
        "Status",
        "Data de Emissão",
        "Data de Validade",
        "Número AET",
        "Transportador"
      ];

      const dataForExport = filteredLicenses.map(license => ({
        "Nº Solicitação": license.requestNumber || '',
        "Tipo de Veículo": translateVehicleType(license.type) || '',
        "Placa Principal": license.mainVehiclePlate || '',
        "Estado": license.state || '',
        "Status": translateStatus(license.status) || '',
        "Data de Emissão": license.emissionDate ? formatDateForCSV(license.emissionDate) : '',
        "Data de Validade": license.validUntil ? formatDateForCSV(license.validUntil) : '',
        "Número AET": license.aetNumber || '',
        "Transportador": license.transporter?.name || license.transporter?.tradeName || `ID: ${license.transporterId}`
      }));

      exportToCSV({
        filename: "licencas-emitidas",
        headers,
        data: dataForExport
      });

      toast({
        title: "Exportação concluída",
        description: `${dataForExport.length} licenças exportadas para CSV`,
      });
    } catch (error) {
      toast({
        title: "Erro na exportação",
        description: "Ocorreu um erro ao exportar os dados",
        variant: "destructive",
      });
    }
  };

  return (
    <MainLayout>
      <div className="mb-6 flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-800">Licenças Emitidas</h1>
          <p className="text-gray-600 mt-1">Histórico de todas as licenças liberadas</p>
        </div>
        <div className="flex gap-2">
          <Button 
            onClick={handleExportCSV}
            variant="outline" 
            className="flex items-center gap-1 bg-white border-gray-200"
            title="Exportar licenças para CSV"
            disabled={isLoading || filteredLicenses.length === 0}
          >
            <Download className="h-4 w-4" />
            Exportar CSV
          </Button>
          <Button 
            onClick={handleRefresh} 
            variant="outline" 
            className={`flex items-center gap-1 bg-white ${isConnected ? 'border-green-200' : 'border-gray-200'}`}
            title={`Atualizar lista de licenças ${isConnected ? '(Tempo real ativo)' : '(Offline)'}`}
            disabled={isRefreshing || isLoading}
          >
            <div className="flex items-center">
              {isRefreshing ? (
                <Loader2 className="h-4 w-4 mr-1 animate-spin" />
              ) : (
                <RefreshCw className="h-4 w-4 mr-1" />
              )}
              {isConnected && (
                <div className="w-2 h-2 bg-green-500 rounded-full mr-1" title="Conectado em tempo real" />
              )}
            </div>
            {isRefreshing ? 'Atualizando...' : 'Atualizar'}
          </Button>
        </div>
      </div>

      <div className="bg-white p-4 rounded-lg shadow mb-6">
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4">
          <div className="sm:col-span-2 lg:col-span-1">
            <label htmlFor="issued-search" className="block text-sm font-medium text-gray-700 mb-1">
              Pesquisar
            </label>
            <div className="relative">
              <Input
                id="issued-search"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Nº do pedido ou placa..."
                className="pl-10"
              />
              <span className="absolute left-3 top-2.5 text-gray-400">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </span>
            </div>
          </div>

          <div>
            <label htmlFor="date-from" className="block text-sm font-medium text-gray-700 mb-1">
              Data Inicial
            </label>
            <Input
              id="date-from"
              type="date"
              value={dateFrom}
              onChange={(e) => setDateFrom(e.target.value)}
            />
          </div>

          <div>
            <label htmlFor="date-to" className="block text-sm font-medium text-gray-700 mb-1">
              Data Final
            </label>
            <Input
              id="date-to"
              type="date"
              value={dateTo}
              onChange={(e) => setDateTo(e.target.value)}
            />
          </div>

          <div>
            <label htmlFor="state-filter" className="block text-sm font-medium text-gray-700 mb-1">
              Estado
            </label>
            <Select value={stateFilter} onValueChange={setStateFilter}>
              <SelectTrigger>
                <SelectValue placeholder="Todos os estados" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all_states">Todos os estados</SelectItem>
                {/* O erro estava aqui: brazilianStates é um objeto no schema, não um array de strings */}
                {['SP', 'MG', 'MT', 'PE', 'TO', 'MS', 'PR', 'ES', 'DNIT', 'RS', 'BA', 'PA', 'SC', 'DF', 'MA', 'GO', 'RJ', 'CE', 'AL', 'SE'].map(state => (
                  <SelectItem key={state} value={state}>{state}</SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div>
            <label htmlFor="status-filter" className="block text-sm font-medium text-gray-700 mb-1">
              Situação
            </label>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger>
                <SelectValue placeholder="Todas as situações" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all_status">Todas as situações</SelectItem>
                <SelectItem value="active">
                  <div className="flex items-center">
                    <CheckCircle2 className="h-3 w-3 mr-2 text-green-500" /> 
                    Ativas
                  </div>
                </SelectItem>
                <SelectItem value="expiring_soon">
                  <div className="flex items-center">
                    <Clock className="h-3 w-3 mr-2 text-amber-500" /> 
                    Vence
                  </div>
                </SelectItem>
                <SelectItem value="expired">
                  <div className="flex items-center">
                    <AlertCircle className="h-3 w-3 mr-2 text-red-500" /> 
                    Vencidas
                  </div>
                </SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>

        {/* Estatísticas rápidas */}
        <div className="grid grid-cols-3 gap-2 mt-4">
          {expandedLicenses.length > 0 && (
            <>
              <div className="text-center py-2 bg-gray-50 rounded-md border border-gray-200">
