 */

import { Pool } from 'pg';
import bcrypt from 'bcrypt';
import dotenv from 'dotenv';

// Carregar variÃ¡veis de ambiente
dotenv.config();

// Usar DATABASE_URL fornecida via linha de comando ou variÃ¡vel de ambiente
const databaseUrl = process.env.DATABASE_URL || 'postgresql://aetuser:nvs123@localhost:5432/aetlicensesystem';

// ConfiguraÃ§Ã£o do banco de dados
const pool = new Pool({
  connectionString: databaseUrl,
  ssl: process.env.NODE_ENV === 'production' ? { rejectUnauthorized: false } : false
});

async function corrigirPermissoes() {
