  console.log('============================================================');

  // Verificar variÃ¡veis de ambiente essenciais
  console.log('ğŸŒ Verificando variÃ¡veis de ambiente...');
  const requiredEnvVars = ['DATABASE_URL', 'NODE_ENV'];
  const missingVars = requiredEnvVars.filter(varName => !process.env[varName]);

  if (missingVars.length > 0) {
    console.log('âŒ VariÃ¡veis de ambiente ausentes:', missingVars.join(', '));
    console.log('ğŸ’¡ Certifique-se de que o arquivo .env estÃ¡ no diretÃ³rio correto');
