      { role: 'financial', podeAcessarBoletos: true, podeCriarTransportadores: true },
      { role: 'manager', podeAcessarBoletos: true, podeCriarTransportadores: true },
      { role: 'admin', podeAcessarBoletos: true, podeCriarTransportadores: true }
    ];

    for (const teste of testesPermissao) {
      // Simular validaÃ§Ã£o de permissÃµes
      const acessoBoletos = ['supervisor', 'financial', 'manager', 'admin'].includes(teste.role);
      const criarTransportadores = teste.role !== 'user';

      const statusBoletos = acessoBoletos === teste.podeAcessarBoletos ? 'âœ…' : 'âŒ';
      const statusTransportadores = criarTransportadores === teste.podeCriarTransportadores ? 'âœ…' : 'âŒ';

      console.log(`  ${teste.role}: Boletos ${statusBoletos} | Transportadores ${statusTransportadores}`);
    }

    console.log('\nğŸ‰ VERIFICAÃ‡ÃƒO DE PERMISSÃ•ES CONCLUÃDA COM SUCESSO!');
    console.log('============================================================');
    console.log('');
    console.log('ğŸ“‹ USUÃRIOS NO SISTEMA:');
    usuarios.forEach(user => {
      console.log(`- ${user.email} (${user.role}) - ${user.full_name}`);
    });
    console.log('');
    console.log('âš ï¸  SENHAS MANTIDAS: As senhas existentes foram preservadas');
    console.log('ğŸ“ Se houver problemas de login, verifique as senhas individualmente');
    console.log('');
    console.log('ğŸ”„ Reinicie o servidor PM2 para aplicar as mudanÃ§as:');
    console.log('   pm2 restart aet-license-system');

  } catch (error) {
    console.error('âŒ Erro durante correÃ§Ã£o de permissÃµes:', error);
    process.exit(1);
  } finally {
    await pool.end();
  }
}

// Executar correÃ§Ã£o se script for chamado diretamente
import { fileURLToPath } from 'url';
import { dirname } from 'path';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

// Verificar se o script estÃ¡ sendo executado diretamente
if (process.argv[1] === __filename) {
