#!/usr/bin/env node

/**
 * Script para importaÃ§Ã£o em massa de transportadores/clientes
 * Uso: node import-clients-script.js dados_clientes.csv
 * 
 * Formato esperado do CSV (separado por ponto e vÃ­rgula):
 * nome;cnpj_cpf;tipo_pessoa;cidade;estado;email;telefone;observacoes
 */

import fs from 'fs';
import csvParser from 'csv-parser';
import { db } from './server/db.js';
import { transporters } from './shared/schema.js';
import bcrypt from 'bcrypt';

const BATCH_SIZE = 10;
const LOG_FILE = `import-clients-${new Date().toISOString().slice(0, 10)}.log`;

function log(message) {
  const timestamp = new Date().toISOString();
  const logMessage = `[${timestamp}] ${message}`;
  console.log(logMessage);
  fs.appendFileSync(LOG_FILE, logMessage + '\n');
}

function formatCNPJCPF(documento) {
  if (!documento) return '';

  // Remove caracteres nÃ£o numÃ©ricos
  const numbers = documento.replace(/\D/g, '');

  if (numbers.length === 11) {
    // CPF: 000.000.000-00
    return numbers.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  } else if (numbers.length === 14) {
    // CNPJ: 00.000.000/0000-00
    return numbers.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  }

  return numbers; // Retorna apenas nÃºmeros se nÃ£o for CPF nem CNPJ
}

function validateCNPJCPF(documento) {
  const numbers = documento.replace(/\D/g, '');
  return numbers.length === 11 || numbers.length === 14;
}

function normalizePersonType(tipo) {
  if (!tipo) return 'pj';

  const tipoLower = tipo.toLowerCase().trim();
  if (tipoLower.includes('fisica') || tipoLower === 'pf' || tipoLower === 'cpf') {
    return 'pf';
  }
  return 'pj';
}

async function processClientData(clientData) {
  try {
    // Validar dados obrigatÃ³rios
    if (!clientData.nome || !clientData.cnpj_cpf) {
      throw new Error('Nome e CNPJ/CPF sÃ£o obrigatÃ³rios');
    }

    if (!validateCNPJCPF(clientData.cnpj_cpf)) {
      throw new Error('CNPJ/CPF invÃ¡lido');
    }

    const documentNumber = clientData.cnpj_cpf.replace(/\D/g, '');

    // Verificar se jÃ¡ existe
    const existing = await db.query.transporters.findFirst({
      where: (transporters, { eq }) => eq(transporters.documentNumber, documentNumber)
    });

    if (existing) {
      log(`âš ï¸  Cliente jÃ¡ existe: ${clientData.nome} (${formatCNPJCPF(documentNumber)})`);
      return { status: 'exists', data: existing };
    }

    // Preparar dados para inserÃ§Ã£o
    const transporterData = {
      name: clientData.nome.trim().toUpperCase(),
      tradeName: clientData.nome_fantasia?.trim() || '',
      personType: normalizePersonType(clientData.tipo_pessoa),
      documentNumber: documentNumber,
      city: clientData.cidade?.trim().toUpperCase() || '',
      state: clientData.estado?.trim().toUpperCase() || '',
      email: clientData.email?.trim().toLowerCase() || '',
      phone: clientData.telefone?.trim() || '',
      subsidiaries: [],
      documents: [],
      isActive: true
    };

    // Inserir no banco
    const [newTransporter] = await db.insert(transporters).values(transporterData).returning();

    log(`âœ… Cliente cadastrado: ${newTransporter.name} (ID: ${newTransporter.id})`);
    return { status: 'created', data: newTransporter };

  } catch (error) {
    log(`âŒ Erro ao processar cliente ${clientData.nome}: ${error.message}`);
    return { status: 'error', error: error.message, data: clientData };
  }
}

async function importClientsFromCSV(filePath) {
  if (!fs.existsSync(filePath)) {
    log(`âŒ Arquivo nÃ£o encontrado: ${filePath}`);
    process.exit(1);
  }

  log(`ğŸ“ Iniciando importaÃ§Ã£o de clientes do arquivo: ${filePath}`);

  const clients = [];
  const results = {
    total: 0,
    created: 0,
    exists: 0,
    errors: 0,
    errorList: []
  };

  return new Promise((resolve, reject) => {
    fs.createReadStream(filePath)
      .pipe(csvParser({ separator: ';' }))
      .on('data', (row) => {
        clients.push({
          nome: row.nome || row.NOME,
          nome_fantasia: row.nome_fantasia || row.NOME_FANTASIA || row['nome fantasia'],
          cnpj_cpf: row.cnpj_cpf || row.CNPJ_CPF || row.cnpj || row.CNPJ || row.cpf || row.CPF,
          tipo_pessoa: row.tipo_pessoa || row.TIPO_PESSOA || row.tipo || row.TIPO,
          cidade: row.cidade || row.CIDADE,
          estado: row.estado || row.ESTADO || row.uf || row.UF,
          email: row.email || row.EMAIL,
          telefone: row.telefone || row.TELEFONE || row.phone || row.PHONE,
          observacoes: row.observacoes || row.OBSERVACOES || row.obs || row.OBS
        });
      })
      .on('end', async () => {
        log(`ğŸ“Š Total de registros lidos: ${clients.length}`);
        results.total = clients.length;

        // Processar em lotes
        for (let i = 0; i < clients.length; i += BATCH_SIZE) {
          const batch = clients.slice(i, i + BATCH_SIZE);
          log(`ğŸ”„ Processando lote ${Math.floor(i / BATCH_SIZE) + 1}/${Math.ceil(clients.length / BATCH_SIZE)}`);

          const batchPromises = batch.map(client => processClientData(client));
          const batchResults = await Promise.all(batchPromises);

          batchResults.forEach(result => {
            if (result.status === 'created') {
              results.created++;
            } else if (result.status === 'exists') {
              results.exists++;
            } else if (result.status === 'error') {
              results.errors++;
              results.errorList.push({
                cliente: result.data?.nome || 'N/A',
                erro: result.error
              });
            }
          });

          // Pequena pausa entre lotes
          await new Promise(resolve => setTimeout(resolve, 100));
        }

        // RelatÃ³rio final
        log(`\nğŸ“ˆ RELATÃ“RIO FINAL:`);
        log(`   Total de registros: ${results.total}`);
        log(`   âœ… Criados: ${results.created}`);
        log(`   âš ï¸  JÃ¡ existiam: ${results.exists}`);
        log(`   âŒ Erros: ${results.errors}`);

        if (results.errors > 0) {
          log(`\nğŸš¨ ERROS DETALHADOS:`);
          results.errorList.forEach((error, index) => {
            log(`   ${index + 1}. ${error.cliente}: ${error.erro}`);
          });
        }

        log(`\nğŸ“„ Log salvo em: ${LOG_FILE}`);
        resolve(results);
      })
      .on('error', (error) => {
        log(`âŒ Erro ao ler arquivo CSV: ${error.message}`);
        reject(error);
      });
  });
}

// Exemplo de uso
async function main() {
  try {
    const csvFile = process.argv[2];

    if (!csvFile) {
      console.log(`
ğŸš€ Script de ImportaÃ§Ã£o de Clientes/Transportadores

Uso: node import-clients-script.js arquivo.csv

Formato CSV esperado (separado por ponto e vÃ­rgula):
nome;cnpj_cpf;tipo_pessoa;cidade;estado;email;telefone;observacoes

Exemplo:
TRANSPORTADORA EXEMPLO LTDA;12.345.678/0001-90;pj;SÃƒO PAULO;SP;contato@exemplo.com;(11) 99999-9999;Cliente VIP

Campos obrigatÃ³rios: nome, cnpj_cpf
Campos opcionais: tipo_pessoa (pf/pj, padrÃ£o: pj), cidade, estado, email, telefone, observacoes

O script irÃ¡:
- Validar CNPJ/CPF
- Verificar duplicatas
- Processar em lotes para melhor performance  
- Gerar log detalhado
- Criar relatÃ³rio final
      `);
      process.exit(1);
    }

    await importClientsFromCSV(csvFile);
    log('ğŸ‰ ImportaÃ§Ã£o concluÃ­da com sucesso!');

  } catch (error) {
    log(`ğŸ’¥ Erro fatal: ${error.message}`);
    process.exit(1);
  }
}

if (import.meta.url === `file://${process.argv[1]}`) {
  main();
}

export { importClientsFromCSV, processClientData };

