// Teste da validaÃ§Ã£o por combinaÃ§Ã£o completa
// Execute: node test-combination-validation.js

const BASE_URL = 'http://localhost:5000';

async function testCombinationValidation() {
  console.log('ğŸ§ª TESTANDO VALIDAÃ‡ÃƒO POR COMBINAÃ‡ÃƒO COMPLETA\n');

  // Dados da licenÃ§a existente (baseado na consulta SQL)
