async function testValidation(estado, composicao, esperaBloqueio) {
  try {
    const response = await fetch(`${BASE_URL}/api/licencas-vigentes-by-state`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        estado: estado,
        composicao: composicao,
        placas: [composicao.cavalo, composicao.carreta1, composicao.carreta2] // Fallback
      })
    });

    if (!response.ok) {
      console.log(`âŒ Erro HTTP ${response.status}: ${response.statusText}`);
      return;
    }

    const data = await response.json();

    console.log(`   ComposiÃ§Ã£o: ${composicao.cavalo} + ${composicao.carreta1} + ${composicao.carreta2}`);
    console.log(`   Estado: ${estado}`);
    console.log(`   Resultado: ${data.bloqueado ? 'ğŸš« BLOQUEADO' : 'âœ… PERMITIDO'}`);

    if (data.bloqueado) {
      console.log(`   LicenÃ§a: ${data.numero_licenca || 'N/A'}`);
      console.log(`   Dias restantes: ${data.diasRestantes || 'N/A'}`);
      console.log(`   Tipo: ${data.tipo_bloqueio || 'padrÃ£o'}`);
    } else {
      console.log(`   Tipo: ${data.tipo_liberacao || 'padrÃ£o'}`);
      console.log(`   Motivo: ${data.message || 'N/A'}`);
    }

    // Verificar se o resultado estÃ¡ conforme esperado
    if (data.bloqueado === esperaBloqueio) {
      console.log(`   âœ… TESTE PASSOU - Resultado conforme esperado`);
    } else {
      console.log(`   âŒ TESTE FALHOU - Esperava ${esperaBloqueio ? 'bloqueio' : 'permissÃ£o'}`);
    }

  } catch (error) {
    console.log(`   âŒ Erro na requisiÃ§Ã£o: ${error.message}`);
  }
}

// Executar os testes
testCombinationValidation().catch(console.error);

