/**
 * Script de validaÃ§Ã£o e correÃ§Ã£o rÃ¡pida para servidor de produÃ§Ã£o
 * VersÃ£o simplificada que foca apenas na validaÃ§Ã£o essencial
 */

import { Pool } from 'pg';
import dotenv from 'dotenv';

// Carregar variÃ¡veis de ambiente
dotenv.config();

// Usar DATABASE_URL fornecida via linha de comando ou variÃ¡vel de ambiente
const databaseUrl = process.env.DATABASE_URL || 'postgresql://aetuser:nvs123@localhost:5432/aetlicensesystem';

const pool = new Pool({
  connectionString: databaseUrl,
  ssl: process.env.NODE_ENV === 'production' ? { rejectUnauthorized: false } : false
});

async function testarValidacao() {
  console.log('ğŸ§ª TESTE RÃPIDO DE VALIDAÃ‡ÃƒO DO SISTEMA');
  console.log('=====================================');

  try {
    // 1. Testar conexÃ£o com banco
    console.log('ğŸ“Š Testando conexÃ£o com banco de dados...');
    const { rows: testConnection } = await pool.query('SELECT NOW() as current_time');
    console.log(`âœ… ConexÃ£o OK - Hora do servidor: ${testConnection[0].current_time}`);

    // 2. Verificar usuÃ¡rios no sistema
    console.log('\nğŸ‘¥ Verificando usuÃ¡rios...');
    const { rows: users } = await pool.query(`
      SELECT email, role, full_name, 
             CASE WHEN password IS NOT NULL AND length(password) > 0 THEN 'configurada' ELSE 'ausente' END as senha_status
      FROM users 
      ORDER BY 
        CASE role 
          WHEN 'admin' THEN 1
          WHEN 'manager' THEN 2
          WHEN 'supervisor' THEN 3
          WHEN 'financial' THEN 4
          WHEN 'operational' THEN 5
          WHEN 'user' THEN 6
        END
    `);

    console.log(`ğŸ“‹ ${users.length} usuÃ¡rios encontrados:`);
    users.forEach(user => {
      console.log(`   ${user.email} (${user.role}) - Senha: ${user.senha_status}`);
    });

    // 3. Verificar variÃ¡veis de ambiente crÃ­ticas
    console.log('\nğŸŒ Verificando configuraÃ§Ã£o do ambiente...');
    const envVars = [
      { name: 'NODE_ENV', value: process.env.NODE_ENV },
      { name: 'DATABASE_URL', value: process.env.DATABASE_URL ? 'configurado' : 'ausente' },
      { name: 'SESSION_SECRET', value: process.env.SESSION_SECRET ? 'configurado' : 'ausente' }
    ];

    envVars.forEach(env => {
      const status = env.value ? 'âœ…' : 'âŒ';
      console.log(`   ${status} ${env.name}: ${env.value || 'nÃ£o configurado'}`);
    });

    // 4. Resumo da validaÃ§Ã£o
    console.log('\nğŸ“‹ RESUMO DA VALIDAÃ‡ÃƒO:');

    const adminUsers = users.filter(u => u.role === 'admin');
    const usersWithPassword = users.filter(u => u.senha_status === 'configurada');

    console.log(`   â€¢ Administradores: ${adminUsers.length}`);
    console.log(`   â€¢ UsuÃ¡rios com senha: ${usersWithPassword.length}/${users.length}`);
    console.log(`   â€¢ Banco de dados: ${testConnection.length > 0 ? 'funcionando' : 'erro'}`);
    console.log(`   â€¢ VariÃ¡veis de ambiente: ${envVars.filter(e => e.value && e.value !== 'ausente').length}/${envVars.length} configuradas`);

    // 5. RecomendaÃ§Ãµes
    console.log('\nğŸ’¡ RECOMENDAÃ‡Ã•ES:');

    if (adminUsers.length === 0) {
      console.log('   âš ï¸  Nenhum administrador encontrado - verifique os roles dos usuÃ¡rios');
    }

    if (!process.env.SESSION_SECRET) {
      console.log('   âš ï¸  SESSION_SECRET nÃ£o configurado - pode causar problemas de autenticaÃ§Ã£o');
    }

    const usersWithoutPassword = users.filter(u => u.senha_status === 'ausente');
    if (usersWithoutPassword.length > 0) {
      console.log(`   âš ï¸  ${usersWithoutPassword.length} usuÃ¡rio(s) sem senha configurada`);
    }

    console.log('\nâœ… VALIDAÃ‡ÃƒO CONCLUÃDA!');
    console.log('Para logs detalhados, use: pm2 logs aet-license-system');

  } catch (error) {
    console.error('\nâŒ ERRO NA VALIDAÃ‡ÃƒO:', error.message);

    if (error.message.includes('connect')) {
      console.log('ğŸ’¡ Problema de conexÃ£o - verifique DATABASE_URL no arquivo .env');
    } else if (error.message.includes('permission')) {
      console.log('ğŸ’¡ Problema de permissÃ£o - verifique as credenciais do banco');
    }

    process.exit(1);
  } finally {
    await pool.end();
  }
}

// Executar se chamado diretamente
import { fileURLToPath } from 'url';
const __filename = fileURLToPath(import.meta.url);

if (process.argv[1] === __filename) {
