import { db, pool } from "./db";
import { v4 as uuidv4 } from "uuid";
import { 
  insertUserSchema, 
  insertVehicleSchema, 
  insertLicenseRequestSchema, 
  insertDraftLicenseSchema, 
  updateLicenseStatusSchema,
  updateLicenseStateSchema,
  insertStatusHistorySchema,
  insertVehicleModelSchema,
  insertBoletoSchema,
  LicenseStatus,
  userRoleEnum,
  licenseRequests,
  transporters,
  statusHistories,
  vehicles,
  boletos,
  stateLicenses
} from "@shared/schema";
import { 
