import { WebSocketServer, WebSocket } from "ws";
import { withCache, invalidateCache, appCache } from "./cache";

// Set up file storage for uploads - configuraÃ§Ã£o robusta para produÃ§Ã£o
const getUploadDir = () => {
  // ConfiguraÃ§Ãµes especÃ­ficas por ambiente
  const possiblePaths = [
    process.env.UPLOAD_DIR, // VariÃ¡vel de ambiente personalizada
    '/home/servidorvoipnvs/uploads', // DiretÃ³rio especÃ­fico do usuÃ¡rio no servidor Google
    '/var/www/uploads', // DiretÃ³rio web padrÃ£o
    '/var/uploads', // PadrÃ£o para produÃ§Ã£o
    '/tmp/uploads', // Fallback temporÃ¡rio
    path.join(process.cwd(), '..', 'uploads'), // Um nÃ­vel acima do projeto
    path.join(process.cwd(), 'storage'), // Dentro do projeto como storage
    path.join(process.cwd(), 'uploads') // Ãšltimo recurso dentro do projeto
  ].filter(Boolean);

  for (const uploadPath of possiblePaths) {
    try {
      // Tentar criar o diretÃ³rio com permissÃµes adequadas
      if (!fs.existsSync(uploadPath!)) {
        fs.mkdirSync(uploadPath!, { recursive: true, mode: 0o755 });
      }

      // Criar subdiretÃ³rios necessÃ¡rios
      const subDirs = ['vehicles', 'transporters', 'boletos'];
      subDirs.forEach(subDir => {
        const subPath = path.join(uploadPath!, subDir);
        if (!fs.existsSync(subPath)) {
          fs.mkdirSync(subPath, { recursive: true, mode: 0o755 });
        }
      });

      // Testar se consegue escrever no diretÃ³rio
      const testFile = path.join(uploadPath!, '.write-test');
      fs.writeFileSync(testFile, 'test');
      fs.unlinkSync(testFile);

      console.log(`[UPLOAD] âœ… Usando diretÃ³rio: ${uploadPath}`);
      console.log(`[UPLOAD] ğŸ“ SubdiretÃ³rios criados: ${subDirs.join(', ')}`);
      return uploadPath!;
    } catch (error) {
      console.warn(`[UPLOAD] âŒ Falha em ${uploadPath}:`, (error as Error).message);
      continue;
    }
  }

  throw new Error('âŒ Nenhum diretÃ³rio de upload vÃ¡lido encontrado');
};

const uploadDir = getUploadDir();

// ConfiguraÃ§Ã£o de storage com lÃ³gica de nomeaÃ§Ã£o especÃ­fica
const storage_config = multer.diskStorage({
  destination: function (req, file, cb) {
    cb(null, uploadDir);
  },
  filename: function (req, file, cb) {
    const ext = path.extname(file.originalname);

    console.log(`[UPLOAD NAMING] Campo: ${file.fieldname}, Arquivo original: ${file.originalname}`);
    console.log(`[UPLOAD NAMING] Dados do request:`, {
      state: req.body?.state,
      aetNumber: req.body?.aetNumber,
      validUntil: req.body?.validUntil
    });

    // Para CRLV de veÃ­culos - manter nome original
    if (file.fieldname === 'crlvFile' || file.fieldname.includes('crlv')) {
      // Sanitizar o nome original para evitar problemas de caracteres especiais
      const originalName = file.originalname.replace(/[^a-zA-Z0-9.-]/g, '_');
      console.log(`[UPLOAD NAMING] CRLV: mantendo nome original sanitizado: ${originalName}`);
      cb(null, originalName);
      return;
    }

    // Para arquivos de estado de licenÃ§as liberadas - MANTER NOME ORIGINAL
    if (file.fieldname === 'stateFile' || file.fieldname.includes('stateFile')) {
      // **NOVA REGRA**: Arquivos de licenÃ§as emitidas devem manter o nome original
      const originalName = file.originalname.replace(/[^a-zA-Z0-9.-]/g, '_');
      console.log(`[UPLOAD NAMING] StateFile: mantendo nome original sanitizado: ${originalName}`);
      cb(null, originalName);
      return;
    }

    // Para outros tipos de arquivos - usar padrÃ£o padrÃ£o
    const uniqueSuffix = Date.now() + '-' + Math.round(Math.random() * 1E9);
    const genericFilename = file.fieldname + '-' + uniqueSuffix + ext;
    console.log(`[UPLOAD NAMING] GenÃ©rico: ${genericFilename}`);
    cb(null, genericFilename);
  }
});

const fileFilter = (req: any, file: Express.Multer.File, cb: multer.FileFilterCallback) => {
  // Accept only images and PDFs
  if (file.mimetype.startsWith('image/') || file.mimetype === 'application/pdf') {
    cb(null, true);
  } else {
    cb(null, false);
  }
};

// Middleware para processar dados do veÃ­culo, tanto de FormData quanto JSON direto
const processVehicleData = (req: any, res: any, next: any) => {
  console.log('Processing request body:', req.body);

  // Se tiver contentType application/json, jÃ¡ estÃ¡ processado como JSON
  const contentType = req.headers['content-type'] || '';

  // Caso 1: Dados no formato FormData com campo vehicleData (abordagem antiga)
  if (req.body && req.body.vehicleData) {
    try {
      if (typeof req.body.vehicleData === 'string' && req.body.vehicleData.trim().length > 0) {
        req.body = {
          ...req.body,
          ...JSON.parse(req.body.vehicleData)
        };
        console.log('Processed vehicle data from vehicleData field:', req.body);
      } else {
        console.error('Campo vehicleData estÃ¡ vazio ou nÃ£o Ã© uma string vÃ¡lida:', req.body.vehicleData);
      }
    } catch (error) {
      console.error('Error parsing vehicleData JSON:', error);
      console.error('ConteÃºdo do campo vehicleData:', req.body.vehicleData);
    }
  } 
  // Caso 2: FormData com campos individuais (nossa nova abordagem)
  else if (contentType.includes('multipart/form-data') && req.body) {
    // Campos individuais jÃ¡ estÃ£o acessÃ­veis em req.body
    console.log('Using form-data fields directly:', req.body);

    // Tratar campos que podem vir como arrays (problema do form-data duplicado)
    Object.keys(req.body).forEach(key => {
      if (Array.isArray(req.body[key])) {
        // Usar o primeiro valor se for array
        req.body[key] = req.body[key][0];
      }
    });

    // Garantir que nÃºmeros sÃ£o convertidos corretamente
    if (req.body.tare) req.body.tare = Number(req.body.tare);
    if (req.body.crlvYear) req.body.crlvYear = Number(req.body.crlvYear);
    if (req.body.year) req.body.year = Number(req.body.year);
    if (req.body.axleCount) req.body.axleCount = Number(req.body.axleCount);
    if (req.body.cmt) req.body.cmt = Number(req.body.cmt);
  }
  // Caso 3: JSON direto (nossa nova abordagem para requests sem arquivo)
  else if (contentType.includes('application/json')) {
    // JÃ¡ processado como JSON pelo bodyParser
    console.log('Request is already in JSON format:', req.body);
  }

  console.log('Final vehicle data for processing:', req.body);
  next();
};

const upload = multer({ 
  storage: storage_config,
  fileFilter,
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB max file size
  }
});

// Upload especÃ­fico para CSV (sem fileFilter)
const uploadCSV = multer({
  storage: multer.memoryStorage(), // Usar memÃ³ria para CSV
  limits: {
    fileSize: 5 * 1024 * 1024, // 5MB max para CSV
  }
});

// Authentication middleware
const requireAuth = (req: any, res: any, next: any) => {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ message: "NÃ£o autenticado" });
  }
  next();
};

// Admin middleware
const requireAdmin = (req: any, res: any, next: any) => {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ message: "NÃ£o autenticado" });
  }

  if (!req.user!.isAdmin) {
    return res.status(403).json({ message: "Acesso negado" });
  }

  next();
};

// Middleware para usuÃ¡rios com papel Operacional
const requireOperational = (req: any, res: any, next: any) => {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ message: "NÃ£o autenticado" });
  }

  // Verifica se o usuÃ¡rio tem papel Operacional, Supervisor ou Admin
  const hasPermission = req.user!.role === 'operational' || 
                       req.user!.role === 'supervisor' || 
                       req.user!.isAdmin;

  if (!hasPermission) {
    return res.status(403).json({ 
      message: "Acesso negado. Apenas usuÃ¡rios com perfil Operacional ou Supervisor podem acessar." 
    });
  }

  next();
};

// Middleware para usuÃ¡rios com papel Supervisor
const requireSupervisor = (req: any, res: any, next: any) => {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ message: "NÃ£o autenticado" });
  }

  // Verifica se o usuÃ¡rio tem papel Supervisor
  if (req.user!.role !== 'supervisor' && !req.user!.isAdmin) {
    return res.status(403).json({ 
      message: "Acesso negado. Apenas usuÃ¡rios com perfil Supervisor podem acessar." 
    });
  }

  next();
};

// Middleware para verificar se o usuÃ¡rio Ã© dono do recurso ou tem papel de staff
const requireOwnerOrStaff = (req: any, res: any, next: any) => {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ message: "NÃ£o autenticado" });
  }

  // Os perfis que podem acessar recursos de outros usuÃ¡rios
  const isStaff = ['operational', 'supervisor'].includes(req.user!.role) || req.user!.isAdmin;

  // Se o usuÃ¡rio nÃ£o Ã© staff, verifica se Ã© o dono do recurso
  if (!isStaff) {
    const resourceUserId = parseInt(req.params.userId);
    if (req.user!.id !== resourceUserId) {
      return res.status(403).json({ 
        message: "Acesso negado. VocÃª sÃ³ pode acessar seus prÃ³prios dados." 
      });
    }
  }

  next();
};

// Tipo para as mensagens WebSocket
interface WSMessage {
  type: 'STATUS_UPDATE' | 'LICENSE_UPDATE' | 'DASHBOARD_UPDATE' | 'VEHICLE_UPDATE' | 'TRANSPORTER_UPDATE' | 'USER_UPDATE' | 'ACTIVITY_LOG_UPDATE' | 'CACHE_INVALIDATION';
  data: any;
}

