export async function registerRoutes(app: Express): Promise<Server> {
  // Log para todas as requisiÃ§Ãµes PATCH
  app.use((req, res, next) => {
    if (req.method === 'PATCH') {
      console.log(`=== PATCH REQUEST: ${req.url} ===`);
    }
    next();
  });

  // Setup authentication routes
  setupAuth(app);

  // Servir arquivos estÃ¡ticos da pasta uploads
  app.use('/uploads', express.static(path.join(process.cwd(), 'uploads')));

  // Criar o servidor HTTP (definido apenas uma vez)
  const httpServer = createServer(app);

  // Configurar o WebSocketServer
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  wss.on('connection', (ws) => {
    console.log('Novo cliente WebSocket conectado');
    wsClients.add(ws);

    ws.on('message', (message) => {
      console.log('Mensagem recebida:', message.toString());
    });

    ws.on('close', () => {
      console.log('Cliente WebSocket desconectado');
      wsClients.delete(ws);
    });

    ws.on('error', (error) => {
      console.error('Erro na conexÃ£o WebSocket:', error);
      wsClients.delete(ws);
    });

    // Enviar mensagem inicial para confirmar conexÃ£o
    ws.send(JSON.stringify({ 
      type: 'CONNECTED', 
      message: 'Conectado ao servidor',
      timestamp: new Date().toISOString()
    }));
  });

  // Cache para armazenar tokens de acesso
  let accessToken: string | undefined = undefined;
  let tokenExpiration: number = 0;

  // FunÃ§Ã£o para obter token de acesso
  async function getAccessToken() {
    try {
      // Verificar se o token atual ainda Ã© vÃ¡lido
      if (accessToken && tokenExpiration > Date.now()) {
        return accessToken;
      }

      // Configurar a solicitaÃ§Ã£o para obter o token
      const tokenUrl = 'https://h-apigateway.conectagov.estaleiro.serpro.gov.br/oauth2/jwt-token';
      const params = new URLSearchParams({
        grant_type: 'client_credentials',
      });

      // Usar chave fornecida pelo cliente
      const authHeader = 'Basic ' + Buffer.from(
        `${process.env.GOV_BR_CLIENT_ID || 'client-id'}:${process.env.GOV_BR_CLIENT_SECRET || 'client-secret'}`
      ).toString('base64');

      // Fazer a solicitaÃ§Ã£o para obter o token
      const response = await fetch(tokenUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          'Authorization': authHeader
        },
        body: params
      });

      if (!response.ok) {
        // Se a resposta nÃ£o for OK, tentar extrair o erro
        const errorText = await response.text();
        console.error('Erro ao obter token de acesso:', errorText);
        throw new Error(`Erro ao obter token: ${response.status} ${response.statusText}`);
      }

      // Extrair o token de acesso da resposta
      const data = await response.json();
      accessToken = data.access_token;
      // Calcular a expiraÃ§Ã£o (normalmente em segundos) e converter para timestamp
      tokenExpiration = Date.now() + (data.expires_in * 1000) - 60000; // 1 minuto antes para evitar problemas

      return accessToken;
    } catch (error) {
      console.error('Erro ao obter token de acesso:', error);
      throw error;
    }
  }

  // Endpoint de API para consulta de CNPJ - usando ReceitaWS
  app.get('/api/external/cnpj/:cnpj', async (req, res) => {
    // Definir explicitamente cabeÃ§alhos para evitar intercepÃ§Ã£o pelo Vite
    res.setHeader('Content-Type', 'application/json');
    res.setHeader('X-Content-Type-Options', 'nosniff');
    try {
      const { cnpj } = req.params;
      const cleanCnpj = cnpj.replace(/[^\d]/g, '');
