      if (composicao && composicao.cavalo && composicao.carreta1 && composicao.carreta2) {
        console.log(`[VALIDAÃ‡ÃƒO DEFINITIVA] Usando validaÃ§Ã£o por combinaÃ§Ã£o especÃ­fica:`, composicao);
      }

      const conflicts = [];

      // Para cada estado, verificar licenÃ§as ativas na tabela licencas_emitidas
      for (const state of states) {
        console.log(`[VALIDAÃ‡ÃƒO DEFINITIVA] Verificando estado: ${state}`);

        let query: string;
        let queryParams: any[];

        // Escolher query baseada na presenÃ§a de composiÃ§Ã£o
        if (composicao && composicao.cavalo && composicao.carreta1 && composicao.carreta2) {
          // Query para combinaÃ§Ã£o especÃ­fica
          query = `
            SELECT 
              le.estado,
              le.numero_licenca,
              le.data_validade,
              le.placa_unidade_tratora,
              le.placa_primeira_carreta,
              le.placa_segunda_carreta,
              le.pedido_id,
              EXTRACT(DAY FROM (le.data_validade - CURRENT_DATE)) as dias_restantes
            FROM licencas_emitidas le
            WHERE le.estado = $1 
              AND le.status = 'ativa'
              AND le.data_validade > CURRENT_DATE
              AND UPPER(le.placa_unidade_tratora) = UPPER($2)
              AND UPPER(le.placa_primeira_carreta) = UPPER($3)
              AND UPPER(le.placa_segunda_carreta) = UPPER($4)
          `;
          queryParams = [state, composicao.cavalo, composicao.carreta1, composicao.carreta2];
          console.log(`[VALIDAÃ‡ÃƒO DEFINITIVA] Verificando combinaÃ§Ã£o especÃ­fica: ${composicao.cavalo} + ${composicao.carreta1} + ${composicao.carreta2}`);
        } else {
          // Query original para qualquer placa
          query = `
            SELECT 
              le.estado,
              le.numero_licenca,
              le.data_validade,
              le.placa_unidade_tratora,
              le.placa_primeira_carreta,
              le.placa_segunda_carreta,
              le.pedido_id,
              EXTRACT(DAY FROM (le.data_validade - CURRENT_DATE)) as dias_restantes
            FROM licencas_emitidas le
            WHERE le.estado = $1 
              AND le.status = 'ativa'
              AND le.data_validade > CURRENT_DATE
              AND (
                le.placa_unidade_tratora = ANY($2::text[]) OR
                le.placa_primeira_carreta = ANY($2::text[]) OR
                le.placa_segunda_carreta = ANY($2::text[])
              )
          `;
          queryParams = [state, plates];
        }

        const result = await pool.query(query, queryParams);

        console.log(`[VALIDAÃ‡ÃƒO DEFINITIVA] Estado ${state}: encontradas ${result.rows.length} licenÃ§as ativas`);

        for (const license of result.rows) {
          const daysUntilExpiry = parseInt(license.dias_restantes);
          console.log(`[VALIDAÃ‡ÃƒO DEFINITIVA] LicenÃ§a ${license.numero_licenca}: ${daysUntilExpiry} dias restantes`);

          // REGRA CRÃTICA: bloquear se tiver mais de 60 dias para evitar custos
          if (daysUntilExpiry > 60) {
            const tipoValidacao = composicao ? 'combinaÃ§Ã£o especÃ­fica' : 'placas individuais';
            console.log(`[VALIDAÃ‡ÃƒO DEFINITIVA] Estado ${state} BLOQUEADO: ${daysUntilExpiry} dias > 60 - EVITANDO CUSTO DESNECESSÃRIO (${tipoValidacao})`);
            conflicts.push({
              state: state,
              licenseNumber: license.numero_licenca,
              expiryDate: license.data_validade,
              daysUntilExpiry: daysUntilExpiry,
