          }
        }
      }

      console.log(`[VALIDAÃ‡ÃƒO DEFINITIVA] Total de conflitos encontrados: ${conflicts.length}`);

      res.json({
        hasConflicts: conflicts.length > 0,
        conflicts,
        message: conflicts.length > 0 
          ? `Encontrados ${conflicts.length} conflito(s) em licenÃ§as vigentes`
          : 'Nenhum conflito encontrado'
      });

    } catch (error) {
      console.error('[VALIDAÃ‡ÃƒO DEFINITIVA] Erro ao verificar licenÃ§as existentes:', error);
