          return res.status(400).json({ message: "Dados do veÃ­culo invÃ¡lidos - JSON malformado" });
        }
      } else {
        // Caso contrÃ¡rio, usar campos individuais
        const { plate, type, tare, crlvYear, status } = req.body;

        if (!plate || !type || !tare || !crlvYear || !status) {
