        issuedAt: req.body.issuedAt, // Incluir data de emissÃ£o
        aetNumber: req.body.aetNumber, // Incluir nÃºmero da AET
        selectedCnpj: req.body.selectedCnpj, // Incluir CNPJ selecionado (global - legado)
        stateCnpj: req.body.stateCnpj, // Incluir CNPJ especÃ­fico para este estado
      };

      console.log('stateStatusData final:', stateStatusData);
      console.log('issuedAt no stateStatusData:', stateStatusData.issuedAt);

      try {
        updateLicenseStateSchema.parse(stateStatusData);
      } catch (error: any) {
        const validationError = fromZodError(error);
        return res.status(400).json({ message: validationError.message });
      }

      // Verificar se a licenÃ§a existe
      const existingLicense = await storage.getLicenseRequestById(licenseId);
      if (!existingLicense) {
        return res.status(404).json({ message: 'LicenÃ§a nÃ£o encontrada' });
      }

      // Verificar se o estado estÃ¡ incluÃ­do na lista de estados da licenÃ§a
      if (!existingLicense.states.includes(stateStatusData.state)) {
        return res.status(400).json({ message: 'Estado nÃ£o incluÃ­do na solicitaÃ§Ã£o da licenÃ§a' });
      }

      // Adicionar arquivo se fornecido
      let file: Express.Multer.File | undefined = undefined;
      if (req.file) {
        file = req.file;
      }

      // Obter o status anterior do estado especÃ­fico
      const previousStateStatus = existingLicense.stateStatuses?.find(ss => 
        ss.startsWith(`${stateStatusData.state}:`)
      )?.split(':')?.[1] || 'pending';

      // Atualizar status do estado da licenÃ§a
      const updatedLicense = await storage.updateLicenseStateStatus({
        licenseId: stateStatusData.licenseId,
        state: stateStatusData.state!,
        status: stateStatusData.status as LicenseStatus,
        comments: stateStatusData.comments,
        validUntil: stateStatusData.validUntil,
        issuedAt: stateStatusData.issuedAt, // Incluir data de emissÃ£o
        aetNumber: stateStatusData.aetNumber,
        selectedCnpj: stateStatusData.selectedCnpj,
        stateCnpj: stateStatusData.selectedCnpj, // Usar selectedCnpj como stateCnpj
        file,
      });

      // Registrar mudanÃ§a no histÃ³rico de status
      await storage.createStatusHistory({
        licenseId: updatedLicense.id,
        state: stateStatusData.state,
        userId: req.user!.id,
        oldStatus: previousStateStatus,
        newStatus: stateStatusData.status,
        comments: stateStatusData.comments || null,
        createdAt: new Date()
      });

      console.log(`HistÃ³rico de status criado para licenÃ§a ${licenseId}, estado ${stateStatusData.state}: ${previousStateStatus} -> ${stateStatusData.status}`);

      // Enviar notificaÃ§Ã£o em tempo real via WebSocket
      broadcastMessage({
        type: 'STATUS_UPDATE',
        data: {
          licenseId: updatedLicense.id,
          state: stateStatusData.state,
          status: stateStatusData.status,
          updatedAt: new Date().toISOString(),
          license: updatedLicense
        }
      });

      console.log(`Status da licenÃ§a ${licenseId} para o estado ${stateStatusData.state} atualizado para ${stateStatusData.status}. NotificaÃ§Ã£o enviada.`);

      res.json(updatedLicense);
    } catch (error) {
      console.error('Error updating license state status:', error);
      res.status(500).json({ message: 'Erro ao atualizar status do estado da licenÃ§a' });
    }
  });

