  app.post("/api/licenses/check-existing", requireAuth, async (req, res) => {
    try {
      const { placas, estados } = req.body;

      console.log("[VALIDAÃ‡ÃƒO] Recebendo verificaÃ§Ã£o de licenÃ§as:", { placas, estados });

      if (!placas || !Array.isArray(placas) || placas.length === 0) {
        return res.status(400).json({ message: "Placas sÃ£o obrigatÃ³rias" });
      }

      if (!estados || !Array.isArray(estados) || estados.length === 0) {
        return res.status(400).json({ message: "Estados sÃ£o obrigatÃ³rios" });
      }

      const conflitos: any[] = [];
      const hoje = new Date();
      const limiteRenovacao = new Date(hoje.getTime() + 30 * 24 * 60 * 60 * 1000); // 30 dias no futuro

      console.log("[VALIDAÃ‡ÃƒO] Data atual:", hoje.toISOString());
      console.log("[VALIDAÃ‡ÃƒO] Limite renovaÃ§Ã£o (30 dias):", limiteRenovacao.toISOString());
      console.log("[VALIDAÃ‡ÃƒO] Placas para verificar:", placas);
      console.log("[VALIDAÃ‡ÃƒO] Estados para verificar:", estados);

      // Buscar todas as licenÃ§as aprovadas vigentes
      const todasLicencas = await db
        .select()
        .from(licenseRequests)
        .where(
          and(
            eq(licenseRequests.status, 'approved'),
            eq(licenseRequests.isDraft, false)
          )
        );

      console.log(`[VALIDAÃ‡ÃƒO] Total de licenÃ§as aprovadas:`, todasLicencas.length);

      // Para cada estado selecionado, verificar individualmente
      for (const estado of estados) {
        console.log(`[VALIDAÃ‡ÃƒO] Verificando estado individual: ${estado}`);

        // Buscar licenÃ§as que incluem este estado especÃ­fico
        for (const licenca of todasLicencas) {
          console.log(`[VALIDAÃ‡ÃƒO] Analisando licenÃ§a ${licenca.id} - Estados: ${JSON.stringify(licenca.states)} - Placa principal: ${licenca.mainVehiclePlate}`);

          // Verificar se a licenÃ§a inclui este estado especÃ­fico
          if (!licenca.states || !licenca.states.includes(estado)) {
            console.log(`[VALIDAÃ‡ÃƒO] LicenÃ§a ${licenca.id} nÃ£o inclui estado ${estado} - PULAR`);
            continue;
          }

          console.log(`[VALIDAÃ‡ÃƒO] âœ“ LicenÃ§a ${licenca.id} inclui estado ${estado}`);

          // Verificar se alguma placa da nova solicitaÃ§Ã£o conflita
          const placaConflitante = placas.find(placa => {
            const conflito = placa === licenca.mainVehiclePlate || 
              (licenca.additionalPlates && licenca.additionalPlates.includes(placa));
            console.log(`[VALIDAÃ‡ÃƒO] Comparando placa ${placa} com licenÃ§a ${licenca.id}: placa principal=${licenca.mainVehiclePlate}, conflito=${conflito}`);
            return conflito;
          });

          if (!placaConflitante) {
            console.log(`[VALIDAÃ‡ÃƒO] LicenÃ§a ${licenca.id} nÃ£o tem conflito de placas - PULAR`);
            continue;
          }

          console.log(`[VALIDAÃ‡ÃƒO] âš ï¸  CONFLITO DE PLACA DETECTADO! LicenÃ§a ${licenca.id}, estado ${estado}, placa ${placaConflitante}`);

          // Verificar se hÃ¡ status aprovado para este estado especÃ­fico
          let dataValidadeEstado = null;
          let statusIndividual = null;

          // Procurar por status individual do estado
          if (licenca.stateStatuses && licenca.stateStatuses.length > 0) {
            statusIndividual = licenca.stateStatuses.find(status => 
              status.startsWith(`${estado}:approved`)
            );

            if (statusIndividual) {
              console.log(`[VALIDAÃ‡ÃƒO] Status individual encontrado para ${estado}:`, statusIndividual);

              // Extrair data de validade especÃ­fica do estado
              const statusParts = statusIndividual.split(':');
              if (statusParts.length > 2 && statusParts[2]) {
                try {
                  dataValidadeEstado = new Date(statusParts[2]);
                  console.log(`[VALIDAÃ‡ÃƒO] Data extraÃ­da do status individual:`, dataValidadeEstado);
                } catch (e) {
                  console.log(`[VALIDAÃ‡ÃƒO] Erro ao parsear data do status:`, statusParts[2]);
                }
              }
            }
          }

          // Se nÃ£o tem status individual mas tem aprovaÃ§Ã£o geral
          if (!dataValidadeEstado && licenca.status === 'approved' && licenca.validUntil) {
            dataValidadeEstado = new Date(licenca.validUntil);
            console.log(`[VALIDAÃ‡ÃƒO] Usando data geral da licenÃ§a:`, dataValidadeEstado);
          }

          if (!dataValidadeEstado) {
            console.log(`[VALIDAÃ‡ÃƒO] LicenÃ§a ${licenca.id} sem data de validade vÃ¡lida`);
            continue;
          }

          // Verificar se ainda estÃ¡ vÃ¡lida
          if (dataValidadeEstado <= hoje) {
            console.log(`[VALIDAÃ‡ÃƒO] LicenÃ§a ${licenca.id} expirada para ${estado}`);
            continue;
          }

          // Verificar se a licenÃ§a no estado especÃ­fico tem mais de 30 dias atÃ© vencer
          const diasRestantes = Math.ceil((dataValidadeEstado.getTime() - hoje.getTime()) / (1000 * 60 * 60 * 24));
          console.log(`[VALIDAÃ‡ÃƒO] Dias restantes para ${estado}:`, diasRestantes);

          if (diasRestantes > 30) {
            console.log(`[VALIDAÃ‡ÃƒO] CONFLITO CONFIRMADO! Estado ${estado}, licenÃ§a ${licenca.id}, dias restantes: ${diasRestantes}`);

            conflitos.push({
