                id: licenca.id,
                requestNumber: licenca.requestNumber,
                mainVehiclePlate: licenca.mainVehiclePlate,
                validUntil: dataValidadeEstado,
                diasRestantes,
                placasConflitantes: [placaConflitante],
                statusIndividual: statusIndividual || `${estado}:approved`
