  async linkTransporterToUser(
    transporterId: number,
    userId: number | null,
  ): Promise<Transporter> {
    const transporter = await this.getTransporterById(transporterId);
    if (!transporter) {
      throw new Error("Transportador nÃ£o encontrado");
    }

    // Se userId for null, estamos apenas removendo a vinculaÃ§Ã£o
    if (userId !== null) {
      const user = await this.getUser(userId);
      if (!user) {
        throw new Error("UsuÃ¡rio nÃ£o encontrado");
      }
    }

    const updatedTransporter = { ...transporter, userId };
    this.transporters.set(transporterId, updatedTransporter);
    return updatedTransporter;
  }

  // Vehicle methods
