    });

    // Filtrar em memÃ³ria para encontrar licenÃ§as com pelo menos um estado aprovado
    const issuedLicenses = allLicenses.filter((license) => {
      // Verificar se o status principal Ã© 'approved'
      if (license.status === "approved") {
