          `[DEBUG] LicenÃ§a #${license.id} incluÃ­da - status principal approved`,
        );
        return true;
      }

      // Verificar se pelo menos um estado tem status 'approved'
      if (license.stateStatuses && Array.isArray(license.stateStatuses)) {
        const hasApprovedState = license.stateStatuses.some((stateStatus) => {
          const isApproved =
            stateStatus.includes(":approved") ||
            stateStatus.includes(":approved:");
          if (isApproved) {
            console.log(
              `[DEBUG] Estado aprovado encontrado em #${license.id}: ${stateStatus}`,
            );
          }
          return isApproved;
        });

        if (hasApprovedState) {
          console.log(
            `[DEBUG] LicenÃ§a #${license.id} incluÃ­da - tem pelo menos um estado approved`,
          );
          return true;
        }
      }

      console.log(
        `[DEBUG] LicenÃ§a #${license.id} excluÃ­da - nÃ£o atende aos critÃ©rios`,
      );
      return false;
    });

    console.log(
      `[DEBUG] getAllIssuedLicenses: Retornando ${issuedLicenses.length} licenÃ§as emitidas`,
    );
    return issuedLicenses;
  }

  async createLicenseRequest(
    userId: number,
    licenseData: InsertLicenseRequest & {
      requestNumber: string;
      isDraft: boolean;
    },
  ): Promise<LicenseRequest> {
    const now = new Date();

    // Sanitizar campos de dimensÃµes e tipo de carga com valores padrÃ£o baseados no tipo de licenÃ§a
    let width = licenseData.width;
    let height = licenseData.height;
    let cargoType = licenseData.cargoType;

    // Se a largura nÃ£o estiver definida, usar valor padrÃ£o com base no tipo de licenÃ§a
    if (width === undefined || width === null) {
      width = licenseData.type === "flatbed" ? 320 : 260; // 3.20m ou 2.60m
    }

    // Se a altura nÃ£o estiver definida, usar valor padrÃ£o com base no tipo de licenÃ§a
    if (height === undefined || height === null) {
      height = licenseData.type === "flatbed" ? 495 : 440; // 4.95m ou 4.40m
    }

    // Se o tipo de carga nÃ£o estiver definido, usar valor padrÃ£o com base no tipo de licenÃ§a
    if (cargoType === undefined || cargoType === null || cargoType === "") {
      cargoType =
        licenseData.type === "flatbed" ? "indivisible_cargo" : "dry_cargo";
    }

    // Garantir que os campos width, height e cargoType sÃ£o tratados corretamente
    const sanitizedData = {
      ...licenseData,
      width: Number(width),
      height: Number(height),
      cargoType,
    };

    console.log("Dados sanitizados no createLicenseRequest:", sanitizedData);

    const license = {
      ...sanitizedData,
      userId,
      createdAt: now,
      updatedAt: now,
    };

    console.log("Dados finais para inserÃ§Ã£o no banco:", license);

    const results = await db
      .insert(licenseRequests)
      .values(license)
      .returning();

    // Log para diagnÃ³stico do que foi efetivamente salvo
    console.log("Resultado da inserÃ§Ã£o no banco:", results[0]);

    return results[0];
  }

