  async updateLicenseDraft(
    id: number,
    draftData: Partial<LicenseRequest>,
  ): Promise<LicenseRequest> {
    const now = new Date();

    // Obter os dados atuais do rascunho para decisÃµes mais informadas sobre valores padrÃ£o
    const currentDraft = await this.getLicenseRequestById(id);
    if (!currentDraft) {
      throw new Error("Rascunho nÃ£o encontrado");
    }

    // Garantir que os campos width, height e cargoType sÃ£o tratados corretamente
    const sanitizedData = {
      ...draftData,
    };

    // Se estamos alterando o tipo de licenÃ§a, podemos precisar atualizar os valores padrÃ£o
    const licenseType = draftData.type || currentDraft.type;

    // Somente converter se os campos estiverem presentes na atualizaÃ§Ã£o
    if (draftData.width !== undefined) {
      sanitizedData.width = Number(draftData.width);
    } else if (
      currentDraft.width === null ||
      currentDraft.width === undefined
    ) {
      // Se o valor atual Ã© null mas nÃ£o estamos atualizando, definir valor padrÃ£o
      sanitizedData.width = licenseType === "flatbed" ? 320 : 260;
    }

    if (draftData.height !== undefined) {
      sanitizedData.height = Number(draftData.height);
    } else if (
      currentDraft.height === null ||
      currentDraft.height === undefined
    ) {
      // Se o valor atual Ã© null mas nÃ£o estamos atualizando, definir valor padrÃ£o
      sanitizedData.height = licenseType === "flatbed" ? 495 : 440;
    }

    if (draftData.cargoType !== undefined) {
      sanitizedData.cargoType = draftData.cargoType;
    } else if (
      currentDraft.cargoType === null ||
      currentDraft.cargoType === undefined ||
      currentDraft.cargoType === ""
    ) {
      // Se o valor atual Ã© null mas nÃ£o estamos atualizando, definir valor padrÃ£o
      sanitizedData.cargoType =
        licenseType === "flatbed" ? "indivisible_cargo" : "dry_cargo";
    }

    console.log("Dados sanitizados no updateLicenseDraft:", sanitizedData);

    const results = await db
      .update(licenseRequests)
      .set({ ...sanitizedData, updatedAt: now })
      .where(eq(licenseRequests.id, id))
      .returning();

    if (results.length === 0) {
      throw new Error("Rascunho nÃ£o encontrado");
    }

    console.log("Resultado da atualizaÃ§Ã£o no banco:", results[0]);

    return results[0];
  }

