  async getDashboardStats(userId: number): Promise<DashboardStats> {
    console.log(`[DASHBOARD NEW] UsuÃ¡rio ${userId} (${(await this.getUser(userId))?.email}) role: ${(await this.getUser(userId))?.role}`);

    // Verificar se o usuÃ¡rio Ã© admin baseado no role
