    if (!user) {
      throw new Error('UsuÃ¡rio nÃ£o encontrado');
    }

    const isAdmin = user.role === 'admin' || user.role === 'supervisor' || user.role === 'manager';

    if (isAdmin) {
      console.log(`[DASHBOARD NEW] ADMIN - Coletando dados globais`);
