    dataEmissao: z.string().refine((date) => !isNaN(Date.parse(date)), {
      message: "Data de emissÃ£o invÃ¡lida",
    }),
    dataVencimento: z.string().refine((date) => !isNaN(Date.parse(date)), {
      message: "Data de vencimento invÃ¡lida",
    }),
    numeroBoleto: z.string().min(1, "NÃºmero do boleto Ã© obrigatÃ³rio"),
    cpfCnpj: z.string().regex(/^(\d{11}|\d{14})$/, "CPF deve ter 11 dÃ­gitos ou CNPJ deve ter 14 dÃ­gitos"),
  })
  .superRefine((val, ctx) => {
    // Validar se data de emissÃ£o Ã© anterior Ã  data de vencimento
    const emissao = new Date(val.dataEmissao);
