import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Calendar, FileText, Upload } from "lucide-react";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";

// Schema para validaÃ§Ã£o do formulÃ¡rio
const boletoSchema = z.object({
  transportadorId: z.number().min(1, "Selecione um transportador"),
  nomeTransportador: z.string().min(1, "Nome do transportador Ã© obrigatÃ³rio"),
  cpfCnpj: z.string().regex(/^(\d{11}|\d{14})$/, "CPF deve ter 11 dÃ­gitos ou CNPJ deve ter 14 dÃ­gitos"),
  numeroBoleto: z.string().min(1, "NÃºmero do boleto Ã© obrigatÃ³rio"),
  valor: z.coerce.number().positive("O valor deve ser positivo"),
  dataEmissao: z.string().refine((date) => !isNaN(Date.parse(date)), {
    message: "Data de emissÃ£o invÃ¡lida",
  }),
  dataVencimento: z.string().refine((date) => !isNaN(Date.parse(date)), {
    message: "Data de vencimento invÃ¡lida",
  }),
  status: z.enum(["aguardando_pagamento", "pago", "vencido"]),
  observacoes: z.string().optional(),
}).refine((val) => {
  const emissao = new Date(val.dataEmissao);
  const vencimento = new Date(val.dataVencimento);
  return emissao < vencimento;
}, {
  message: "A data de emissÃ£o deve ser anterior Ã  data de vencimento",
  path: ["dataVencimento"],
});

type BoletoFormData = z.infer<typeof boletoSchema>;

interface Transporter {
  id: number;
  name: string;
  documentNumber: string;
}

interface Boleto {
  id: number;
  transportadorId: number;
  nomeTransportador: string;
  cpfCnpj: string;
  numeroBoleto: string;
  valor: string;
  dataEmissao: string;
  dataVencimento: string;
  status: string;
  uploadBoletoUrl?: string;
  uploadNfUrl?: string;
  observacoes?: string;
}

interface BoletoFormProps {
  boleto?: Boleto | null;
  onSuccess: () => void;
  onCancel: () => void;
}

function BoletoForm({ boleto, onSuccess, onCancel }: BoletoFormProps) {
  const [uploadBoleto, setUploadBoleto] = useState<File | null>(null);
  const [uploadNf, setUploadNf] = useState<File | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Buscar transportadores para o select
  const { data: transporters = [] } = useQuery<Transporter[]>({
    queryKey: ["/api/admin/transporters"],
  });

  const form = useForm<BoletoFormData>({
    resolver: zodResolver(boletoSchema),
    defaultValues: {
      transportadorId: boleto?.transportadorId || 0,
      nomeTransportador: boleto?.nomeTransportador || "",
      cpfCnpj: boleto?.cpfCnpj || "",
      numeroBoleto: boleto?.numeroBoleto || "",
      valor: boleto ? parseFloat(boleto.valor) : 0,
      dataEmissao: boleto?.dataEmissao ? new Date(boleto.dataEmissao).toISOString().split('T')[0] : "",
      dataVencimento: boleto?.dataVencimento ? new Date(boleto.dataVencimento).toISOString().split('T')[0] : "",
      status: (boleto?.status as any) || "aguardando_pagamento",
      observacoes: boleto?.observacoes || "",
    },
  });

  const createMutation = useMutation({
    mutationFn: (data: FormData) => apiRequest("/api/boletos", "POST", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/boletos"] });
      toast({
        title: "Sucesso",
        description: "Boleto criado com sucesso",
      });
      onSuccess();
    },
    onError: (error: any) => {
      toast({
        title: "Erro",
        description: error.message || "Erro ao criar boleto",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: (data: FormData) => apiRequest(`/api/boletos/${boleto!.id}`, "PUT", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/boletos"] });
      toast({
        title: "Sucesso",
        description: "Boleto atualizado com sucesso",
      });
      onSuccess();
    },
    onError: (error: any) => {
      toast({
        title: "Erro",
        description: error.message || "Erro ao atualizar boleto",
        variant: "destructive",
      });
    },
  });

  // Atualizar dados do transportador quando selecionado
  const handleTransporterChange = (transporterId: string) => {
    const transporter = transporters.find(t => t.id === parseInt(transporterId));
    if (transporter) {
      form.setValue("transportadorId", transporter.id);
      form.setValue("nomeTransportador", transporter.name);
      form.setValue("cpfCnpj", transporter.documentNumber);
    }
  };

  const onSubmit = (data: BoletoFormData) => {
    const formData = new FormData();

    // Adicionar dados do boleto
    Object.entries(data).forEach(([key, value]) => {
      if (value !== undefined && value !== null) {
        formData.append(key, value.toString());
      }
    });

    // Adicionar arquivos se fornecidos
    if (uploadBoleto) {
      formData.append("uploadBoleto", uploadBoleto);
    }
    if (uploadNf) {
      formData.append("uploadNf", uploadNf);
    }

    if (boleto) {
      updateMutation.mutate(formData);
    } else {
      createMutation.mutate(formData);
    }
  };

  const isLoading = createMutation.isPending || updateMutation.isPending;

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {/* SeleÃ§Ã£o de Transportador */}
          <FormField
            control={form.control}
            name="transportadorId"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Transportador</FormLabel>
                <Select
                  value={field.value?.toString()}
                  onValueChange={handleTransporterChange}
                >
                  <FormControl>
                    <SelectTrigger>
                      <SelectValue placeholder="Selecione um transportador" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    {transporters.map((transporter) => (
                      <SelectItem key={transporter.id} value={transporter.id.toString()}>
                        {transporter.name} - {transporter.documentNumber}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* NÃºmero do Boleto */}
          <FormField
            control={form.control}
            name="numeroBoleto"
            render={({ field }) => (
              <FormItem>
                <FormLabel>NÃºmero do Boleto</FormLabel>
                <FormControl>
                  <Input {...field} placeholder="Ex: 001234567890" />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Valor */}
          <FormField
            control={form.control}
            name="valor"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Valor (R$)</FormLabel>
                <FormControl>
                  <Input
                    type="number"
                    step="0.01"
                    min="0"
                    {...field}
                    placeholder="0,00"
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Status */}
          <FormField
            control={form.control}
            name="status"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Status</FormLabel>
                <Select value={field.value} onValueChange={field.onChange}>
                  <FormControl>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    <SelectItem value="aguardando_pagamento">Aguardando Pagamento</SelectItem>
                    <SelectItem value="pago">Pago</SelectItem>
                    <SelectItem value="vencido">Vencido</SelectItem>
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Data de EmissÃ£o */}
          <FormField
            control={form.control}
            name="dataEmissao"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Data de EmissÃ£o</FormLabel>
                <FormControl>
                  <Input type="date" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Data de Vencimento */}
          <FormField
            control={form.control}
            name="dataVencimento"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Data de Vencimento</FormLabel>
                <FormControl>
                  <Input type="date" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        {/* Upload de Arquivos */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <Label htmlFor="uploadBoleto">Arquivo do Boleto (PDF)</Label>
            <div className="mt-1">
              <Input
                id="uploadBoleto"
                type="file"
                accept=".pdf"
                onChange={(e) => setUploadBoleto(e.target.files?.[0] || null)}
                className="file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
              />
              {boleto?.uploadBoletoUrl && (
                <p className="text-sm text-gray-500 mt-1">
                  Arquivo atual: <a href={boleto.uploadBoletoUrl} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline">Ver arquivo</a>
                </p>
              )}
            </div>
          </div>

          <div>
            <Label htmlFor="uploadNf">Nota Fiscal (PDF)</Label>
            <div className="mt-1">
              <Input
                id="uploadNf"
                type="file"
                accept=".pdf"
                onChange={(e) => setUploadNf(e.target.files?.[0] || null)}
                className="file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-green-50 file:text-green-700 hover:file:bg-green-100"
              />
              {boleto?.uploadNfUrl && (
                <p className="text-sm text-gray-500 mt-1">
                  Arquivo atual: <a href={boleto.uploadNfUrl} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline">Ver arquivo</a>
                </p>
              )}
            </div>
          </div>
        </div>

        {/* ObservaÃ§Ãµes */}
        <FormField
          control={form.control}
          name="observacoes"
          render={({ field }) => (
            <FormItem>
              <FormLabel>ObservaÃ§Ãµes</FormLabel>
              <FormControl>
                <Textarea
                  {...field}
                  placeholder="InformaÃ§Ãµes adicionais sobre o boleto..."
                  rows={3}
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* BotÃµes de AÃ§Ã£o */}
        <div className="flex justify-end gap-3">
          <Button type="button" variant="outline" onClick={onCancel}>
            Cancelar
          </Button>
          <Button type="submit" disabled={isLoading}>
            {isLoading ? "Salvando..." : boleto ? "Atualizar" : "Criar Boleto"}
          </Button>
        </div>
      </form>
    </Form>
  );
}

export { BoletoForm };

client/src/hooks/use-auth.tsx.content:
import { createContext, ReactNode, useContext, useRef } from "react";
import {
  useQuery,
  useMutation,
  UseMutationResult,
} from "@tanstack/react-query";
import { insertUserSchema, User } from "@shared/schema";
import { getQueryFn, apiRequest, queryClient } from "../lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { z } from "zod";

type AuthContextType = {
  user: User | null;
  isLoading: boolean;
  error: Error | null;
  loginMutation: UseMutationResult<User, Error, LoginData>;
  logoutMutation: UseMutationResult<void, Error, void>;
  registerMutation: UseMutationResult<User, Error, RegisterData>;
  checkRole: (role: 'admin' | 'supervisor' | 'operational' | 'financial') => boolean;
};

const loginSchema = z.object({
  email: z.string().email("Por favor insira um e-mail vÃ¡lido"),
  password: z.string().min(6, "A senha deve ter pelo menos 6 caracteres"),
});

const registerSchema = insertUserSchema.extend({
  confirmPassword: z.string().min(6, "A confirmaÃ§Ã£o de senha deve ter pelo menos 6 caracteres"),
}).refine((data) => data.password === data.confirmPassword, {
  message: "As senhas nÃ£o coincidem",
  path: ["confirmPassword"],
});

type LoginData = z.infer<typeof loginSchema>;
type RegisterData = z.infer<typeof registerSchema>;

export const AuthContext = createContext<AuthContextType | null>(null);

export function AuthProvider({ children }: { children: ReactNode }) {
  const { toast } = useToast();
  const logoutInProgress = useRef(false);
  const {
    data: user,
    error,
    isLoading,
  } = useQuery<User | null, Error>({
    queryKey: ["/api/user"],
    queryFn: getQueryFn({ on401: "returnNull" }),
  });

  // Helper para verificar os papÃ©is de usuÃ¡rio
  const checkRole = (role: 'admin' | 'supervisor' | 'operational' | 'financial'): boolean => {
    if (!user) return false;

    switch (role) {
      case 'admin':
        return user.role === 'admin' || !!user.isAdmin;
      case 'supervisor':
        return user.role === 'admin' || user.role === 'supervisor' || !!user.isAdmin;
      case 'operational':
        return user.role === 'admin' || user.role === 'supervisor' || user.role === 'operational' || !!user.isAdmin;
      case 'financial':
        return user.role === 'admin' || user.role === 'financial' || !!user.isAdmin;
      default:
        return false;
    }
  };

  const loginMutation = useMutation({
    mutationFn: async (credentials: LoginData) => {
      const res = await apiRequest("POST", "/api/login", credentials);
      return await res.json();
    },
    onSuccess: (user: User) => {
      queryClient.setQueryData(["/api/user"], user);
      toast({
        title: "Login realizado com sucesso",
        description: `Bem-vindo(a), ${user.fullName}!`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Falha no login",
        description: error.message || "Credenciais invÃ¡lidas",
        variant: "destructive",
      });
    },
  });

  const registerMutation = useMutation({
    mutationFn: async (userData: RegisterData) => {
      // Remove confirmPassword before sending to the server
      const { confirmPassword, ...userDataToSend } = userData;
      const res = await apiRequest("POST", "/api/register", userDataToSend);
      return await res.json();
    },
    onSuccess: (user: User) => {
      queryClient.setQueryData(["/api/user"], user);
      toast({
        title: "Cadastro realizado com sucesso",
        description: `Bem-vindo(a), ${user.fullName}!`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Falha no cadastro",
        description: error.message || "NÃ£o foi possÃ­vel criar sua conta",
        variant: "destructive",
      });
    },
  });

  const logoutMutation = useMutation({
    mutationFn: async () => {
      // Previne mÃºltiplas execuÃ§Ãµes simultÃ¢neas
      if (logoutInProgress.current) return;
      logoutInProgress.current = true;

      try {
        // Limpa o cache imediatamente para logout instantÃ¢neo
        queryClient.setQueryData(["/api/user"], null);
        queryClient.clear(); // Remove todos os dados em cache

        // Faz a requisiÃ§Ã£o de logout em background
        await fetch("/api/logout", {
          method: "POST",
          credentials: "include"
        });
      } catch (error) {
        // Silencia erros de logout - usuÃ¡rio jÃ¡ foi deslogado localmente
        console.warn("Erro no logout do servidor:", error);
      } finally {
        // Reset da flag apÃ³s um pequeno delay para evitar cliques duplos
        setTimeout(() => {
          logoutInProgress.current = false;
        }, 1000);
      }
    },
    onSuccess: () => {
      toast({
        title: "Logout realizado",
        description: "VocÃª foi desconectado.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Falha no logout",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  return (
    <AuthContext.Provider
      value={{
        user: user || null,
        isLoading,
        error,
        loginMutation,
        logoutMutation,
        registerMutation,
        checkRole,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
}

export { loginSchema, registerSchema };

