import { useState, useCallback } from 'react';
import { apiRequest } from '@/lib/queryClient';

interface Placas {
  cavalo?: string;
  primeiraCarreta?: string;
  segundaCarreta?: string;
  dolly?: string;
  prancha?: string;
  reboque?: string;
}

interface LicencaConflito {
  estado: string;
  numero: string;
  validade: string;
  diasRestantes: number;
  placas: string[];
}

export function useLicenseValidationFinal() {
  const [estadosBloqueados, setEstadosBloqueados] = useState<Record<string, LicencaConflito>>({});
  const [isChecking, setIsChecking] = useState(false);

  const validarEstados = useCallback(async (estados: string[], placas: Placas): Promise<string[]> => {
    if (!estados || estados.length === 0 || !placas) {
      return [];
    }

    const placasArray = Object.values(placas).filter(Boolean);
    if (placasArray.length === 0) {
      return [];
    }

    console.log(`[VALIDAÃ‡ÃƒO FINAL] Verificando estados:`, estados, 'com placas:', placasArray);

    setIsChecking(true);
    const estadosLiberados: string[] = [];
    const novosEstadosBloqueados: Record<string, LicencaConflito> = {};

    try {
      // Verificar cada estado individualmente para robustez
      for (const estado of estados) {
        try {
          console.log(`[VALIDAÃ‡ÃƒO FINAL] Verificando estado: ${estado}`);

          // Usar consulta SQL direta via endpoint especÃ­fico
          const response = await fetch('/api/validacao-critica', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            credentials: 'include',
            body: JSON.stringify({
              estado: estado,
              placas: placasArray
            })
          });

          if (response.ok) {
            const data = await response.json();
            console.log(`[VALIDAÃ‡ÃƒO FINAL] Resposta para ${estado}:`, data);

            if (data.bloqueado && data.diasRestantes > 60) {
              console.log(`[VALIDAÃ‡ÃƒO FINAL] ${estado} BLOQUEADO: ${data.diasRestantes} dias > 60`);
              novosEstadosBloqueados[estado] = {
                estado: estado,
                numero: data.numero,
                validade: data.validade,
                diasRestantes: data.diasRestantes,
                placas: data.placasConflitantes || placasArray
              };
            } else {
              console.log(`[VALIDAÃ‡ÃƒO FINAL] ${estado} LIBERADO: ${data.diasRestantes || 0} dias â‰¤ 60`);
              estadosLiberados.push(estado);
            }
          } else {
            console.error(`[VALIDAÃ‡ÃƒO FINAL] Erro HTTP ${response.status} para ${estado}`);
            // Em caso de erro, liberar o estado (fail-safe)
            estadosLiberados.push(estado);
          }
        } catch (error) {
          console.error(`[VALIDAÃ‡ÃƒO FINAL] Erro ao verificar ${estado}:`, error);
          // Em caso de erro, liberar o estado (fail-safe)
          estadosLiberados.push(estado);
        }
      }

      // Atualizar estados bloqueados
      setEstadosBloqueados(novosEstadosBloqueados);

      console.log(`[VALIDAÃ‡ÃƒO FINAL] Resultado: ${estadosLiberados.length} liberados, ${Object.keys(novosEstadosBloqueados).length} bloqueados`);

      return estadosLiberados;

    } catch (error) {
      console.error('[VALIDAÃ‡ÃƒO FINAL] Erro geral:', error);
      // Em caso de erro geral, liberar todos os estados
      return estados;
    } finally {
      setIsChecking(false);
    }
  }, []);

  const getEstadosBloqueados = useCallback(() => {
    return estadosBloqueados;
  }, [estadosBloqueados]);

  const isEstadoBloqueado = useCallback((estado: string) => {
    return estadosBloqueados[estado] !== undefined;
  }, [estadosBloqueados]);

  const clearValidation = useCallback(() => {
    setEstadosBloqueados({});
  }, []);

  return {
    validarEstados,
    getEstadosBloqueados,
    isEstadoBloqueado,
    clearValidation,
    isChecking,
    estadosBloqueados
  };
}

import { useState, useCallback } from 'react';

interface Placas {
  cavalo?: string;
  primeiraCarreta?: string;
  segundaCarreta?: string;
  dolly?: string;
  prancha?: string;
  reboque?: string;
}

interface LicencaVigente {
  numero_licenca: string;
  data_validade: string;
  diasRestantes: number;
  bloqueado: boolean;
  placas: {
    tratora: string | null;
    primeira: string | null;
    segunda: string | null;
    dolly: string | null;
    prancha: string | null;
    reboque: string | null;
  };
}

interface EstadoBloqueado {
  numero: string;
  validade: string;
  diasRestantes: number;
}

interface ComposicaoVeicular {
  cavalo: string;
  carreta1: string;
  carreta2: string;
}

export function useLicenseValidationV2() {
  const [estadosBloqueados, setEstadosBloqueados] = useState<Record<string, EstadoBloqueado>>({});
  const [isChecking, setIsChecking] = useState(false);

  const verificarEstadoComLicencaVigente = useCallback(async (estado: string, placas: Placas, composicao?: ComposicaoVeicular): Promise<boolean> => {
    if (!estado || !placas) return false;

    setIsChecking(true);

    try {
      // Converter placas object para array de strings
      const placasArray = Object.values(placas).filter(Boolean);

      console.log(`[VALIDAÃ‡ÃƒO INTELIGENTE] Verificando estado: ${estado} com placas:`, placas);

      if (placasArray.length === 0) {
        console.log(`[VALIDAÃ‡ÃƒO INTELIGENTE] Nenhuma placa fornecida`);
        return false;
      }

      // âœ… NOVA LÃ“GICA: ValidaÃ§Ã£o por combinaÃ§Ã£o especÃ­fica
      let requestBody: any = {
        estado: estado,
        placas: placasArray
      };

      if (composicao && composicao.cavalo && composicao.carreta1 && composicao.carreta2) {
        console.log(`[VALIDAÃ‡ÃƒO COMBINAÃ‡ÃƒO] Usando validaÃ§Ã£o por combinaÃ§Ã£o especÃ­fica:`, composicao);
        requestBody.composicao = composicao;

        // Usar endpoint de validaÃ§Ã£o por combinaÃ§Ã£o especÃ­fica
        const response = await fetch('/api/licencas-vigentes-by-combination', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
          body: JSON.stringify({
            estado: estado,
            composicao: composicao
          })
        });

        if (!response.ok) {
          const errorText = await response.text();
          console.error(`[VALIDAÃ‡ÃƒO COMBINAÃ‡ÃƒO] Erro na requisiÃ§Ã£o ${response.status}:`, errorText);
          return false;
        }

        const data = await response.json();
        console.log(`[VALIDAÃ‡ÃƒO COMBINAÃ‡ÃƒO] Resposta da API para ${estado}:`, data);

        if (data.bloqueado && data.diasRestantes > 60) {
          console.log(`[VALIDAÃ‡ÃƒO COMBINAÃ‡ÃƒO] Estado ${estado} BLOQUEADO: ${data.diasRestantes} dias > 60 - COMBINAÃ‡ÃƒO IDÃŠNTICA`);
          setEstadosBloqueados((prev) => ({
            ...prev,
            [estado]: {
              numero: data.numero_licenca,
              validade: data.data_validade,
              diasRestantes: data.diasRestantes
            }
          }));
          return true;
        }

        console.log(`[VALIDAÃ‡ÃƒO COMBINAÃ‡ÃƒO] Estado ${estado} LIBERADO - CombinaÃ§Ã£o diferente ou dentro do prazo`);
        return false;
      }

      // VALIDAÃ‡ÃƒO TRADICIONAL: Consulta na tabela licencas_emitidas por placas individuais
      const response = await fetch('/api/licencas-vigentes-by-state', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(requestBody)
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`[VALIDAÃ‡ÃƒO CRÃTICA] Erro na requisiÃ§Ã£o ${response.status}:`, errorText);
        return false;
      }

      const data = await response.json();
      console.log(`[VALIDAÃ‡ÃƒO CRÃTICA] Resposta da API para ${estado}:`, data);

      // Nova lÃ³gica baseada no endpoint especÃ­fico por estado
      if (data.bloqueado && data.diasRestantes > 60) {
        console.log(`[VALIDAÃ‡ÃƒO CRÃTICA] Estado ${estado} BLOQUEADO: ${data.diasRestantes} dias > 60 - EVITANDO CUSTO DESNECESSÃRIO`);
        setEstadosBloqueados((prev) => ({
          ...prev,
          [estado]: {
            numero: data.numero_licenca,
            validade: data.data_validade,
            diasRestantes: data.diasRestantes
          }
        }));
        return true; // Estado bloqueado
      }

      console.log(`[VALIDAÃ‡ÃƒO CRÃTICA] Estado ${estado} LIBERADO: ${data.bloqueado ? `${data.diasRestantes} dias â‰¤ 60` : 'sem licenÃ§a vigente'}`);
      // Remover o estado dos bloqueados se estava bloqueado antes
      setEstadosBloqueados((prev) => {
        const updated = { ...prev };
        delete updated[estado];
        return updated;
      });
      return false; // Estado liberado
    } catch (error) {
      console.error('Erro na validaÃ§Ã£o:', error);
      return false; // Em caso de erro, liberar
    } finally {
      setIsChecking(false);
    }
  }, []);

  const limparValidacao = useCallback(() => {
    setEstadosBloqueados({});
  }, []);

  const diasRestantes = useCallback((dataValidade: string): number => {
    const now = new Date();
    const validade = new Date(dataValidade);
    return Math.ceil((validade.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));
  }, []);

  const formatarData = useCallback((dataISO: string): string => {
    const data = new Date(dataISO);
    return data.toLocaleDateString('pt-BR');
  }, []);

  return {
    estadosBloqueados,
    setEstadosBloqueados,
    isChecking,
    verificarEstadoComLicencaVigente,
    limparValidacao,
    diasRestantes,
    formatarData
  };
}

