import { useState } from 'react';

export interface LicenseConflict {
  state: string;
  licenseId: number;
  requestNumber: string;
  aetNumber: string;
  validUntil: string;
  daysUntilExpiry: number;
  conflictingPlates: string[];
  canRenew: boolean;
}

interface ValidationResponse {
  hasConflicts: boolean;
  conflicts: LicenseConflict[];
  message: string;
}

export function useLicenseValidation() {
  const [isValidating, setIsValidating] = useState(false);
  const [validationResult, setValidationResult] = useState<ValidationResponse | null>(null);

  const validateLicenses = async (states: string[], plates: string[]): Promise<ValidationResponse> => {
    setIsValidating(true);

    try {
      console.log(`[VALIDAÃ‡ÃƒO FRONTEND] Verificando estados: ${states.join(', ')} e placas: ${plates.join(', ')}`);

      const response = await fetch('/api/licenses/check-existing', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          states,
          plates
        }),
      });

      if (!response.ok) {
        throw new Error(`Erro na validaÃ§Ã£o: ${response.status}`);
      }

      const result: ValidationResponse = await response.json();

      console.log(`[VALIDAÃ‡ÃƒO FRONTEND] Resultado: ${result.hasConflicts ? 'CONFLITOS ENCONTRADOS' : 'SEM CONFLITOS'}`);
      console.log(`[VALIDAÃ‡ÃƒO FRONTEND] Total de conflitos: ${result.conflicts.length}`);

      setValidationResult(result);
      return result;

    } catch (error) {
      console.error('[VALIDAÃ‡ÃƒO FRONTEND] Erro ao validar licenÃ§as:', error);
      const errorResult: ValidationResponse = {
        hasConflicts: false,
        conflicts: [],
        message: `Erro ao verificar licenÃ§as existentes: ${error}`
      };
      setValidationResult(errorResult);
      return errorResult;
    } finally {
      setIsValidating(false);
    }
  };

  const clearValidation = () => {
    setValidationResult(null);
  };

  return {
    isValidating,
    validationResult,
    validateLicenses,
    clearValidation,
  };
}

