  const { checkRole } = useAuth();

  // Para usuÃ¡rios administrativos, redireciona para admin/licenses
  // Para usuÃ¡rios normais, vai para "/"
  const redirectPath = checkRole('operational') ? "/admin/licenses" : "/";

  return (
    <Route path={path}>
      <Redirect to={redirectPath} />
    </Route>
  );
};

// Rota para qualquer usuÃ¡rio autenticado
export function ProtectedRoute({
  path,
  component: Component,
}: {
  path: string;
  component: () => React.JSX.Element;
}) {
  const { user, isLoading } = useAuth();

  if (isLoading) {
    return <LoadingRoute path={path} />;
  }

  if (!user) {
    return <RedirectToAuth path={path} />;
  }

  // Passa a chave Ãºnica para o componente para ajudar na identificaÃ§Ã£o
  // e evitar reconstruÃ§Ã£o desnecessÃ¡ria
  return <Route path={path}>
    <Component />
  </Route>;
}

// Rota para administradores do sistema
export function AdminRoute({
  path,
  component: Component,
}: {
  path: string;
  component: () => React.JSX.Element;
}) {
  const { user, isLoading, checkRole } = useAuth();
  const { toast } = useToast();
  const [isAdmin, setIsAdmin] = useState<boolean | null>(null);
  const [isCheckingAdmin, setIsCheckingAdmin] = useState(true);

  useEffect(() => {
    const checkAdmin = async () => {
      if (!user) return;

      try {
        // Verifica direto usando o helper para casos Ã³bvios
        if (checkRole('admin')) {
          setIsAdmin(true);
          setIsCheckingAdmin(false);
          return;
        }

        // Caso nÃ£o seja Ã³bvio, faz uma verificaÃ§Ã£o no servidor
        const res = await fetch("/api/admin/check", {
          credentials: "include",
        });

        if (res.ok) {
          setIsAdmin(true);
        } else {
          setIsAdmin(false);
          toast({
            title: "Acesso negado",
            description: "VocÃª nÃ£o tem permissÃ£o para acessar esta pÃ¡gina",
            variant: "destructive",
          });
        }
      } catch (error) {
        setIsAdmin(false);
      } finally {
        setIsCheckingAdmin(false);
      }
    };

    if (user) {
      checkAdmin();
    } else {
      setIsCheckingAdmin(false);
    }
  }, [user, toast, checkRole]);

  if (isLoading || isCheckingAdmin) {
    return <LoadingRoute path={path} />;
  }

  if (!user) {
    return <RedirectToAuth path={path} />;
  }

  if (!isAdmin) {
    return <RedirectToHome path={path} />;
  }

  return <Route path={path}>
    <Component />
  </Route>;
}

// Rota apenas para usuÃ¡rios Operacionais e Supervisores
export function StaffRoute({
  path,
  component: Component,
  requiredRole = "any"
}: {
  path: string;
  component: () => React.JSX.Element;
  requiredRole?: "operational" | "supervisor" | "financial" | "any";
}) {
  const { user, isLoading, checkRole } = useAuth();
  const { toast } = useToast();
  const [isAuthorized, setIsAuthorized] = useState<boolean | null>(null);
  const [isChecking, setIsChecking] = useState(true);

  useEffect(() => {
    const checkPermission = async () => {
      if (!user) return;

      try {
        // Verifica diretamente usando o helper para verificaÃ§Ã£o rÃ¡pida
        if (requiredRole === "supervisor" && checkRole('supervisor')) {
          setIsAuthorized(true);
          setIsChecking(false);
          return;
        }

        if (requiredRole === "operational" && checkRole('operational')) {
          setIsAuthorized(true);
          setIsChecking(false);
          return;
        }

        if (requiredRole === "financial" && checkRole('financial')) {
          setIsAuthorized(true);
          setIsChecking(false);
          return;
        }

        // Se nÃ£o for Ã³bvio, faz a verificaÃ§Ã£o no servidor
        const endpoint = 
          requiredRole === "supervisor" 
            ? "/api/staff/check-supervisor"
            : requiredRole === "financial"
            ? "/api/staff/check-financial"
            : "/api/staff/check-operational";

        const res = await fetch(endpoint, {
          credentials: "include",
        });

        if (res.ok) {
          setIsAuthorized(true);
        } else {
          setIsAuthorized(false);
          toast({
            title: "Acesso restrito",
            description: "VocÃª nÃ£o tem permissÃ£o para acessar esta funcionalidade",
            variant: "destructive",
          });
        }
      } catch (error) {
        setIsAuthorized(false);
        toast({
          title: "Erro de verificaÃ§Ã£o",
          description: "NÃ£o foi possÃ­vel verificar suas permissÃµes",
          variant: "destructive",
        });
      }

      setIsChecking(false);
    };

    if (user) {
      checkPermission();
    } else {
      setIsChecking(false);
    }
  }, [user, toast, requiredRole, checkRole]);

  if (isLoading || isChecking) {
    return <LoadingRoute path={path} />;
  }

  if (!user) {
    return <RedirectToAuth path={path} />;
  }

  if (!isAuthorized) {
    return <RedirectToHome path={path} />;
  }

  return <Route path={path}>
    <Component />
  </Route>;
}

// Rota apenas para usuÃ¡rios Supervisores
export function SupervisorRoute({
  path,
  component: Component,
}: {
  path: string;
  component: () => React.JSX.Element;
}) {
  return (
    <StaffRoute 
      path={path} 
      component={Component} 
      requiredRole="supervisor" 
    />
  );
}

// Rota apenas para usuÃ¡rios Operacionais
export function OperationalRoute({
  path,
  component: Component,
}: {
  path: string;
  component: () => React.JSX.Element;
}) {
  return (
    <StaffRoute 
      path={path} 
      component={Component} 
      requiredRole="operational" 
    />
  );
}

