import { QueryClient, QueryFunction } from "@tanstack/react-query";

async function throwIfResNotOk(res: Response) {
  if (!res.ok) {
    try {
      // Tentar extrair erro como JSON primeiro se possÃ­vel
      const contentType = res.headers.get('content-type');
      if (contentType && contentType.includes('application/json')) {
        const errorJson = await res.json();
        throw new Error(errorJson.message || `${res.status}: ${res.statusText}`);
      } else {
        const text = await res.text();
        throw new Error(text || `${res.status}: ${res.statusText}`);
      }
    } catch (e) {
      // Se nÃ£o conseguir parsear ou outro erro
      if (e instanceof Error) {
        throw e;
      }
      throw new Error(`${res.status}: ${res.statusText}`);
    }
  }
}

/**
 * FunÃ§Ã£o para realizar requisiÃ§Ãµes Ã  API com tratamento de erro padronizado
 */
export async function apiRequest(
  method: string,
  url: string,
  data?: unknown | undefined,
  options?: { 
    headers?: Record<string, string>;
    isFormData?: boolean;
  }
): Promise<Response> {
  try {
    // Verifica se Ã© FormData diretamente ou pela flag
    const isFormData = data instanceof FormData || options?.isFormData === true;
    const headers = options?.headers || {};

    // NÃ£o definimos Content-Type para FormData, o navegador define automaticamente com o boundary correto
    if (data && !isFormData && !headers['Content-Type']) {
      headers['Content-Type'] = 'application/json';
    }

    const res = await fetch(url, {
      method,
      headers,
      // Para FormData nÃ£o usamos JSON.stringify
      body: isFormData ? (data as BodyInit) : data ? JSON.stringify(data) : undefined,
      credentials: "include",
    });

    await throwIfResNotOk(res);
    return res;
  } catch (error) {
    console.error(`Erro na requisiÃ§Ã£o ${method} ${url}:`, error);
    throw error;
  }
}

/**
 * Comportamentos possÃ­veis quando ocorre erro 401 (nÃ£o autenticado)
 */
type UnauthorizedBehavior = "returnNull" | "throw";

/**
 * FunÃ§Ã£o de query para o TanStack Query com tratamento de erros
 */
export const getQueryFn: <T>(options: {
  on401: UnauthorizedBehavior;
}) => QueryFunction<T> =
  ({ on401: unauthorizedBehavior }) =>
  async ({ queryKey }) => {
    try {
      const url = queryKey[0] as string;
      const res = await fetch(url, {
        method: 'GET',
        credentials: "include",
        headers: {
          'Accept': 'application/json'
        }
      });

      // Tratamento especÃ­fico para erros de autenticaÃ§Ã£o
      if (res.status === 401) {
        if (unauthorizedBehavior === "returnNull") {
          return null;
        } else {
          throw new Error("NÃ£o autenticado");
        }
      }

      await throwIfResNotOk(res);
      return await res.json();
    } catch (error) {
      console.error(`Erro na consulta:`, error);
      throw error;
    }
  };

/**
 * Cliente de query configurado com os padrÃµes da aplicaÃ§Ã£o
 */
export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      queryFn: getQueryFn({ on401: "throw" }),
      // Performance otimizada: cache reduzido para logout mais rÃ¡pido
      staleTime: 30 * 1000, // 30 segundos apenas
      // Cache reduzido para limpeza mais rÃ¡pida
      gcTime: 2 * 60 * 1000, // 2 minutos apenas
      // Desabilita refetch automÃ¡tico para melhor performance
      refetchOnWindowFocus: false,
      refetchOnReconnect: false,
      // Retry mais agressivo mas rÃ¡pido
      retry: (failureCount, error: any) => {
        // NÃ£o retry em erros de autenticaÃ§Ã£o
        if (error?.message?.includes('401') || error?.message?.includes('NÃ£o autenticado')) {
          return false;
        }
        return failureCount < 2;
      },
      retryDelay: 500, // Delay menor entre tentativas
      // Prefere dados em cache para navegaÃ§Ã£o mais rÃ¡pida
      refetchOnMount: false,
      // Network timeout menor para evitar delays
      networkMode: 'online',
    },
    mutations: {
      retry: 1,
      networkMode: 'online',
    },
  },
});

