    'SC': 'Santa Catarina',
    'RS': 'Rio Grande do Sul',
    'MS': 'Mato Grosso do Sul',
    'MT': 'Mato Grosso',
    'GO': 'GoiÃ¡s',
    'DF': 'Distrito Federal',
    'BA': 'Bahia',
    'SE': 'Sergipe',
    'AL': 'Alagoas',
    'PE': 'Pernambuco',
    'PB': 'ParaÃ­ba',
    'RN': 'Rio Grande do Norte',
    'CE': 'CearÃ¡',
    'PI': 'PiauÃ­',

    'PA': 'ParÃ¡',
    'AP': 'AmapÃ¡',
    'AM': 'Amazonas',
    'RR': 'Roraima',
