// Verificar se o usuÃ¡rio Ã© admin
export function isAdminUser(user: any): boolean {
  return user?.isAdmin === true || user?.role === 'admin';
}

// Verificar se o usuÃ¡rio Ã© operacional
export function isOperationalUser(user: any): boolean {
  return user?.role === 'operational' || user?.role === 'supervisor';
}

// Formatar data para exibiÃ§Ã£o no formato brasileiro
export function formatDate(dateString: string | Date | undefined | null): string {
  if (!dateString) return "-";

  try {
    let date: Date;

    if (typeof dateString === "string") {
      // Se a string contÃ©m 'T', Ã© ISO format
      if (dateString.includes('T')) {
        date = new Date(dateString);
      } else {
        // Se Ã© formato YYYY-MM-DD, processar diretamente
        const parts = dateString.split('-');
        if (parts.length === 3) {
          date = new Date(parseInt(parts[0]), parseInt(parts[1]) - 1, parseInt(parts[2]));
        } else {
          date = new Date(dateString);
        }
      }
    } else {
      date = dateString;
    }

    if (!(date instanceof Date) || isNaN(date.getTime())) {
      return "-";
    }

    return date.toLocaleDateString("pt-BR", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
    });
  } catch (error) {
    console.error("Erro ao formatar data:", error, dateString);
    return "-";
  }
}

// Formatar moeda brasileira
export function formatCurrency(value: number): string {
  return new Intl.NumberFormat("pt-BR", {
    style: "currency",
    currency: "BRL",
  }).format(value);
}

// Verificar se o usuÃ¡rio pode acessar funcionalidades financeiras
export function canAccessFinancial(user: any): boolean {
  return user?.role === "admin" || user?.role === "financial" || user?.role === "manager";
}

