      setTimeout(() => {
        queryClient.invalidateQueries({ queryKey: ["/api/admin/licenses"] });
      }, 500);
    },
    onError: (error: Error) => {
      toast({
        title: "Erro",
        description: error.message || "NÃ£o foi possÃ­vel atualizar o status do estado",
        variant: "destructive",
      });
