                                  <SelectItem value="under_review">AnÃ¡lise do Ã“rgÃ£o</SelectItem>
                                  <SelectItem value="pending_approval">Pendente LiberaÃ§Ã£o</SelectItem>
                                  <SelectItem value="approved">Liberada</SelectItem>
                                </SelectContent>
                              </Select>
                            </div>

                            <div>
                              <Label htmlFor="state-comments">ComentÃ¡rios para {selectedState}</Label>
                              <Textarea 
                                id="state-comments"
                                value={stateComments}
                                onChange={(e) => setStateComments(e.target.value)}
