              <DialogFooter className="gap-2 sm:gap-0">
                <Button variant="outline" onClick={() => setSelectedLicense(null)}>
                  Fechar
                </Button>
                {/* O botÃ£o de salvar agora aparece apenas em cada aba especÃ­fica */}
              </DialogFooter>
            </DialogContent>
          </Dialog>
        )}
      </main>
    </div>
  );
}

