import { useState } from "react";
import { useForm } from "react-hook-form";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Upload, FileText, CheckCircle, XCircle, AlertTriangle, Download } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { MainLayout } from "@/components/layout/main-layout";

interface ImportResult {
  success: boolean;
  inserted: number;
  errors: ImportError[];
  validVehicles: any[];
}

interface ImportError {
  row: number;
  data: any;
  error: string;
}

export function BulkVehiclesPage() {
  const [csvFile, setCsvFile] = useState<File | null>(null);
  const [previewData, setPreviewData] = useState<any[]>([]);
  const [importResult, setImportResult] = useState<ImportResult | null>(null);
  const [isDragOver, setIsDragOver] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { register, handleSubmit, formState: { errors } } = useForm();

  const importMutation = useMutation({
    mutationFn: async (formData: FormData) => {
      const response = await fetch("/api/vehicles/bulk-import", {
        method: "POST",
        body: formData,
        credentials: "include", // Importante para enviar cookies de autenticaÃ§Ã£o
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ message: "Erro desconhecido" }));
        throw new Error(errorData.message || "Erro ao processar arquivo CSV");
      }
      return response.json();
    },
    onSuccess: (result) => {
      setImportResult(result);
      if (result.success) {
        toast({
          title: "ImportaÃ§Ã£o concluÃ­da",
          description: `${result.inserted} veÃ­culos importados com sucesso`,
        });
        queryClient.invalidateQueries({ queryKey: ["/api/vehicles"] });
      }
    },
    onError: (error: any) => {
      toast({
        title: "Erro na importaÃ§Ã£o",
        description: error.message || "Erro ao processar arquivo CSV",
        variant: "destructive",
      });
    },
  });

  const handleFileSelect = (file: File) => {
    setCsvFile(file);
    setImportResult(null);

    // Preview dos primeiros registros
    const reader = new FileReader();
    reader.onload = (e) => {
      const text = e.target?.result as string;
      const lines = text.split('\n').slice(0, 6); // Primeira linha (header) + 5 linhas de dados
      const preview = lines.map(line => line.split(';'));
      setPreviewData(preview);
    };
    reader.readAsText(file);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);

    const file = e.dataTransfer.files[0];
    if (file && file.type === 'text/csv') {
      handleFileSelect(file);
    } else {
      toast({
        title: "Arquivo invÃ¡lido",
        description: "Por favor, selecione um arquivo CSV vÃ¡lido",
        variant: "destructive",
      });
    }
  };

  const onSubmit = () => {
    console.log('[FRONTEND] onSubmit chamado, csvFile:', csvFile);

    if (!csvFile) {
      toast({
        title: "Arquivo necessÃ¡rio",
        description: "Por favor, selecione um arquivo CSV",
        variant: "destructive",
      });
      return;
    }

    const formData = new FormData();
    formData.append('csvFile', csvFile);

    console.log('[FRONTEND] FormData criado:', {
      hasFile: formData.has('csvFile'),
      fileName: csvFile.name,
      fileSize: csvFile.size
    });

    importMutation.mutate(formData);
  };

  const downloadTemplate = () => {
    const template = [
      'placa;tipo_veiculo;marca;modelo;ano_fabricacao;ano_crlv;renavam;cmt;tara;eixo;transportador_cpf_cnpj',
      'ABC1D23;Unidade Tratora (Cavalo);Scania;R440;2018;2024;12345678901;45000;10500;5;12345678000199',
      'DEF4E56;Primeira Carreta;Randon;RK-430SR;2019;2024;12345678902;25000;8500;3;12345678000199'
    ].join('\n');

    const blob = new Blob([template], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = 'modelo_veiculos.csv';
    link.click();
  };

  return (
