              trend={4}
              trendText="em processamento"
              color="yellow"
            />
            <StatsCard 
              title="VeÃ­culos Cadastrados"
              value={stats?.registeredVehicles || 0}
              icon={<Truck className={`${isMobile ? 'h-6 w-6' : 'h-8 w-8'}`} />}
              secondaryText={`${stats?.activeVehicles || 0} ativos`}
              color="blue"
            />
          </div>
        )}

        <Card className="mb-8">
          <div className="p-6 border-b border-border">
            <h2 className="text-lg font-medium text-foreground">LicenÃ§as Recentes</h2>
          </div>
          <LicenseTable licenses={stats?.recentLicenses || []} isLoading={isLoading} />
          <div className="px-6 py-4 border-t border-border">
            <a href="/issued-licenses" className="text-sm text-primary hover:text-primary/80 font-medium">Ver todas as licenÃ§as â†’</a>
          </div>
        </Card>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <Card className="p-6">
            <Tabs defaultValue="vehicle-status">
              <TabsList className="mb-4">
                <TabsTrigger value="vehicle-status">Status de VeÃ­culos</TabsTrigger>
                <TabsTrigger value="license-states">LicenÃ§as por Estado</TabsTrigger>
              </TabsList>
              <TabsContent value="vehicle-status">
                <div className="h-64">
                  <StatusChart
                    type="vehicle"
                    isLoading={isLoading}
                  />
                </div>
              </TabsContent>
              <TabsContent value="license-states">
                <div className="h-64">
                  <StatusChart
                    type="state"
                    isLoading={isLoading}
                  />
                </div>
              </TabsContent>
            </Tabs>
          </Card>

          <Card className="p-6">
            <h2 className="text-lg font-medium text-foreground mb-4">LicenÃ§as por Estado</h2>
            <div className="h-64">
              <StatusChart
                type="state"
                isLoading={isLoading}
              />
            </div>
          </Card>
        </div>
      </PageTransition>
    </MainLayout>
  );
}

