import { useAuth } from "@/hooks/use-auth";
import { Loader2 } from "lucide-react";

/**
 * Componente que redireciona usuÃ¡rios com base em seus papÃ©is
 * UsuÃ¡rios administrativos (Admin, Supervisor, Operacional) vÃ£o para /admin/licenses
 * UsuÃ¡rios comuns vÃ£o para o dashboard
 */
export default function RedirectPage() {
  const [, setLocation] = useLocation();
  const { user, checkRole, isLoading } = useAuth();

  useEffect(() => {
    if (!isLoading && user) {
      // Se Ã© usuÃ¡rio administrativo, vai para a pÃ¡gina de gerenciar licenÃ§as
      if (checkRole('operational')) {
        setLocation("/admin/licenses");
      } else {
        // SenÃ£o, vai para o dashboard do usuÃ¡rio normal
        setLocation("/dashboard");
      }
    }
  }, [user, isLoading, checkRole, setLocation]);

