import { useState, useEffect, useMemo } from "react";
import { MainLayout } from "@/components/layout/main-layout";
import { Input } from "@/components/ui/input";
import { FileDown, CheckCircle, Search, Download, RefreshCw } from "lucide-react";
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue
} from "@/components/ui/select";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { LicenseRequest } from "@shared/schema";
import { LicenseList } from "@/components/licenses/license-list";
import { Dialog, DialogContent, DialogTitle, DialogHeader, DialogDescription } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { StatusBadge } from "@/components/licenses/status-badge";
import { ProgressFlow, StateProgressFlow } from "@/components/licenses/progress-flow";
import { format } from "date-fns";
import { getLicenseTypeLabel } from "@/lib/utils";
import { useToast } from "@/hooks/use-toast";
import { TransporterInfo } from "@/components/transporters/transporter-info";
import { SortableHeader } from "@/components/ui/sortable-header";
import { LicenseDetailsCard } from "@/components/licenses/license-details-card";
import { exportToCSV, formatDateForCSV } from "@/lib/csv-export";
import { usePaginatedList } from "@/hooks/use-paginated-list";
import { ListPagination, MobileListPagination } from "@/components/ui/list-pagination";
import { brazilianStates } from "@shared/schema";

export default function TrackLicensePage() {
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("");
  const [dateFilter, setDateFilter] = useState("");
  const [stateFilter, setStateFilter] = useState("");
  const queryClient = useQueryClient();
  const [selectedLicense, setSelectedLicense] = useState<LicenseRequest | null>(null);
  const [sortColumn, setSortColumn] = useState<string | null>("createdAt");
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc' | null>('desc');

  const { toast } = useToast();

  // Buscamos todas as licenÃ§as nÃ£o finalizadas usando a rota /api/licenses
  // Rascunhos de renovaÃ§Ã£o nÃ£o devem aparecer aqui
  const { data: licenses, isLoading, refetch } = useQuery<LicenseRequest[]>({
    queryKey: ["/api/licenses"],
    queryFn: async () => {
      // Incluir rascunhos de renovaÃ§Ã£o para que apareÃ§am em tempo real
      const res = await fetch("/api/licenses?includeRenewal=true", {
        credentials: "include"
      });
      if (!res.ok) {
        throw new Error("Erro ao buscar licenÃ§as");
      }

      const data = await res.json();

      // Filtrar para remover quaisquer rascunhos de renovaÃ§Ã£o que possam ter passado pelo filtro do backend
      const filteredData = data.filter((license) => {
        // Exclui qualquer licenÃ§a que seja rascunho E tenha 'RenovaÃ§Ã£o' no campo comments
        return !(license.isDraft && license.comments?.includes('RenovaÃ§Ã£o'));
      });

      return filteredData;
    },
    // TEMPO REAL: Reduzir stale time e habilitar refetch automÃ¡tico
    staleTime: 30 * 1000, // 30 segundos
    refetchInterval: 60 * 1000, // Refetch a cada 60 segundos
    refetchOnWindowFocus: true,
    refetchOnMount: true,
    retry: 1
  });

  // Usado para notificar o usuÃ¡rio sobre a disponibilidade de dados em cache
  useEffect(() => {
    if (licenses && licenses.length > 0) {
      toast({
        title: "LicenÃ§as carregadas",
        description: `${licenses.length} licenÃ§as disponÃ­veis para consulta.`,
        duration: 3000,
      });
    }
  }, [licenses, toast]);

  // Otimizado usando useMemo para evitar recÃ¡lculos desnecessÃ¡rios
  // Criar interface estendida para a licenÃ§a com estado especÃ­fico
  interface ExtendedLicense extends Omit<LicenseRequest, 'transporterId'> {
    specificState?: string;
    specificStateStatus?: string;
    specificStateFileUrl?: string;
    stateValidUntil?: string;
    // Sobrescrever transporterId para aceitar null tambÃ©m
    transporterId: number | null;
  }

  // FunÃ§Ã£o para atualizar os dados
  const handleRefresh = () => {
    refetch();
    // Toast para feedback ao usuÃ¡rio
    toast({
      title: "Atualizando dados",
      description: "Buscando as informaÃ§Ãµes mais recentes das licenÃ§as.",
      duration: 2000,
    });
  };

  // FunÃ§Ã£o para ordenar licenÃ§as
  const handleSort = (column: string) => {
    if (sortColumn === column) {
      // Se jÃ¡ estÃ¡ ordenando por esta coluna, alterna a direÃ§Ã£o
      if (sortDirection === 'asc') {
        setSortDirection('desc');
      } else if (sortDirection === 'desc') {
        setSortDirection('asc');
      } else {
        setSortDirection('asc');
      }
    } else {
      // Nova coluna selecionada, comeÃ§a com ascendente
      setSortColumn(column);
      setSortDirection('asc');
    }
  };

  interface ExtendedLicenseWithId extends ExtendedLicense {
    uniqueId?: string;
  }

  // Criar uma lista expandida de licenÃ§as separadas por estado (sem duplicaÃ§Ã£o quando ordenadas)
  const expandedLicenses = useMemo(() => {
    if (!licenses) return [];

    const result: ExtendedLicenseWithId[] = [];

    licenses.forEach(license => {
      // Para cada estado na licenÃ§a, crie uma entrada especÃ­fica
      if (license.states && license.states.length > 0) {
        license.states.forEach((state, index) => {
          // Verificar o status para este estado especÃ­fico
          const stateStatusEntry = license.stateStatuses?.find(ss => ss.startsWith(`${state}:`));
          const stateStatus = stateStatusEntry?.split(':')[1];

          // Verificar se temos uma data de validade no formato estado:status:data
          const stateValidUntil = stateStatusEntry && stateStatusEntry.split(':').length > 2 ? 
            stateStatusEntry.split(':')[2] : undefined;

          // Verificar se existe um arquivo especÃ­fico para este estado
          const stateFileEntry = license.stateFiles?.find(sf => sf.startsWith(`${state}:`));
          const stateFileUrl = stateFileEntry ? stateFileEntry.split(':').slice(1).join(':') : undefined;

          // Criar uma cÃ³pia da licenÃ§a com o estado especÃ­fico e um ID Ãºnico
          const stateLicense: ExtendedLicenseWithId = {
            ...license,
            specificState: state,
            // Substituir o array de estados com apenas este estado
            states: [state],
            // Para filtros de status no frontend, usamos o status do estado especÃ­fico
            specificStateStatus: stateStatus,
            // URL do arquivo deste estado especÃ­fico
            specificStateFileUrl: stateFileUrl,
            // Incluir data de validade especÃ­fica para este estado
            stateValidUntil: stateValidUntil,
            // ID Ãºnico para esta licenÃ§a expandida
            uniqueId: `${license.id}-${state}`
          };

          result.push(stateLicense);
        });
      } else {
        // Se nÃ£o houver estados, apenas adicione a licenÃ§a como estÃ¡ com ID Ãºnico
        result.push({
          ...license,
          uniqueId: `${license.id}-default`
        });
      }
    });

    return result;
  }, [licenses]);

  // Aplicar filtros Ã  lista expandida
  const filteredLicenses = useMemo(() => {
    return expandedLicenses.filter(license => {
      const matchesSearch = !searchTerm || 
        license.requestNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
        license.mainVehiclePlate.toLowerCase().includes(searchTerm.toLowerCase());

      // Filtragem especÃ­fica por status (geral ou estado especÃ­fico)
      let matchesStatus = !statusFilter || statusFilter === "all_status";

      if (statusFilter && statusFilter !== "all_status") {
        // Para o status "Pedido em Cadastramento", verificar licenÃ§as sem status especÃ­fico de estado
        if (statusFilter === "pending_registration") {
          // LicenÃ§a sem status especÃ­fico ou com status geral pending_registration
          matchesStatus = (!license.specificStateStatus || license.specificStateStatus === "pending_registration") &&
                        (!license.stateStatuses || license.stateStatuses.length === 0 || license.status === "pending_registration");
        } else {
          // Para outros status, verificar status especÃ­fico do estado primeiro, depois o status geral
          matchesStatus = (license.specificStateStatus === statusFilter) || 
                         (license.status === statusFilter);
        }
      }

      const matchesDate = !dateFilter || (
        license.createdAt && 
        format(new Date(license.createdAt), "yyyy-MM-dd") === dateFilter
      );

      const matchesState = !stateFilter || stateFilter === "all_states" ||
        license.specificState === stateFilter;

      return matchesSearch && matchesStatus && matchesDate && matchesState;
    });
  }, [expandedLicenses, searchTerm, statusFilter, dateFilter, stateFilter]);

  // Ordenar licenÃ§as filtradas (sem duplicaÃ§Ãµes)
  const sortedLicenses = useMemo(() => {
    if (!sortColumn || !sortDirection) {
      return filteredLicenses;
    }

    // Criar uma cÃ³pia para ordenaÃ§Ã£o
    const toSort = [...filteredLicenses];

    // Definir uma funÃ§Ã£o de ordenaÃ§Ã£o personalizada com base na coluna e direÃ§Ã£o
    const getSortValue = (license: ExtendedLicenseWithId, column: string): any => {
      if (column === 'status') {
        return license.specificStateStatus || license.status;
      } else if (column === 'state') {
        return license.specificState || (license.states && license.states.length > 0 ? license.states[0] : '');
      } else if (column === 'requestNumber') {
        return license.requestNumber;
      } else if (column === 'type') {
        return license.type;
      } else if (column === 'mainVehiclePlate') {
        return license.mainVehiclePlate;
      } else if (column === 'createdAt') {
        return license.createdAt ? new Date(license.createdAt).getTime() : 0;
      } else if (column === 'updatedAt') {
        return license.updatedAt ? new Date(license.updatedAt).getTime() : 0;
      } else if (column === 'validUntil') {
        // Ordenar por data de validade, priorizando a do estado especÃ­fico
        const dateStr = license.stateValidUntil || license.validUntil;
        return dateStr ? new Date(dateStr).getTime() : 0;
      } else {
        return license[column as keyof typeof license];
      }
    };

    // Ordenar o array
    toSort.sort((a, b) => {
      const aValue = getSortValue(a, sortColumn);
      const bValue = getSortValue(b, sortColumn);

      // Valores iguais
      if (aValue === bValue) return 0;

      // Tratamento para nulos
      if (aValue === null || aValue === undefined) {
        return sortDirection === 'asc' ? 1 : -1;
      }
      if (bValue === null || bValue === undefined) {
        return sortDirection === 'asc' ? -1 : 1;
      }

      // Para strings
      if (typeof aValue === 'string' && typeof bValue === 'string') {
        return sortDirection === 'asc' 
          ? aValue.localeCompare(bValue) 
          : bValue.localeCompare(aValue);
      }

      // Para nÃºmeros e outros tipos
      return sortDirection === 'asc' 
        ? (aValue < bValue ? -1 : 1) 
        : (bValue < aValue ? -1 : 1);
    });

    return toSort;
  }, [filteredLicenses, sortColumn, sortDirection]);

  // Hook de paginaÃ§Ã£o aplicado Ã s licenÃ§as ordenadas
  const {
    paginatedItems: paginatedLicenses,
    pagination,
    currentPage,
    setCurrentPage,
    searchTerm: paginationSearchTerm,
    setSearchTerm: setPaginationSearchTerm
  } = usePaginatedList<ExtendedLicenseWithId>({
    items: sortedLicenses,
    itemsPerPage: 10
  });

  const handleViewLicense = (license: LicenseRequest) => {
    setSelectedLicense(license);
  };

  // FunÃ§Ã£o para traduzir tipos de veÃ­culos
  const translateVehicleType = (type: string): string => {
    const typeMap: { [key: string]: string } = {
      'truck': 'CaminhÃ£o',
      'bitruck': 'Bitruck',
      'tractor': 'Cavalo MecÃ¢nico',
      'bitrain_9_axles': 'Bitrem 9 Eixos',
      'road_train': 'Rodotrem',
      'simple_trailer': 'Reboque Simples',
      'semi_trailer': 'Semirreboque',
      'dolly': 'Dolly',
      'platform': 'Prancha'
    };
    return typeMap[type] || type;
  };

  // FunÃ§Ã£o para traduzir status
  const translateStatus = (status: string): string => {
    const statusMap: { [key: string]: string } = {
      'pending_registration': 'Pedido em Cadastramento',
      'registration_in_progress': 'Cadastro em Andamento',
      'pending_documentation': 'Pendente DocumentaÃ§Ã£o',
      'rejected': 'Reprovado',
      'under_review': 'AnÃ¡lise do Ã“rgÃ£o',
      'pending_approval': 'Pendente LiberaÃ§Ã£o',
      'approved': 'Liberada',
      'canceled': 'Cancelado'
    };
    return statusMap[status] || status;
  };

  // FunÃ§Ã£o para exportar CSV
  const handleExportCSV = () => {
    try {
      const headers = [
        "NÂº SolicitaÃ§Ã£o",
        "Tipo de VeÃ­culo",
        "Placa Principal",
        "Estado",
        "Status",
        "Data de SolicitaÃ§Ã£o",
        "Ãšltima AtualizaÃ§Ã£o"
      ];

      const dataForExport = paginatedLicenses.map(license => ({
        "NÂº SolicitaÃ§Ã£o": license.requestNumber || '',
        "Tipo de VeÃ­culo": translateVehicleType(license.type) || '',
        "Placa Principal": license.mainVehiclePlate || '',
        "Estado": license.specificState || (license.states?.join(', ')) || '',
        "Status": translateStatus(license.specificStateStatus || license.status) || '',
        "Data de SolicitaÃ§Ã£o": formatDateForCSV(license.createdAt),
        "Ãšltima AtualizaÃ§Ã£o": formatDateForCSV(license.updatedAt)
      }));

      exportToCSV({
        filename: "acompanhar-licencas",
        headers,
        data: dataForExport
      });

      toast({
        title: "ExportaÃ§Ã£o concluÃ­da",
        description: `${dataForExport.length} licenÃ§as exportadas para CSV`,
      });
    } catch (error) {
      toast({
        title: "Erro na exportaÃ§Ã£o",
        description: "Ocorreu um erro ao exportar os dados",
        variant: "destructive",
      });
    }
  };

  return (
    <MainLayout>
      <div className="mb-6 flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-800">Acompanhar LicenÃ§a</h1>
          <p className="text-gray-600 mt-1">Acompanhe o status de todas as suas licenÃ§as solicitadas</p>
        </div>
        <div className="flex gap-2">
          <Button 
            onClick={handleExportCSV}
            variant="outline" 
            className="flex items-center gap-1 bg-white border-gray-200"
            title="Exportar licenÃ§as para CSV"
            disabled={isLoading || sortedLicenses.length === 0}
          >
            <Download className="h-4 w-4" />
            Exportar CSV
          </Button>
          <Button 
            onClick={handleRefresh} 
            variant="outline" 
            className="flex items-center gap-1 bg-white"
            title="Atualizar lista de licenÃ§as"
          >
            <RefreshCw className="h-4 w-4 mr-1" />
            Atualizar
          </Button>
        </div>
      </div>

      <div className="bg-white p-4 rounded-lg shadow mb-6">
        <div className="flex flex-wrap gap-4">
          <div className="w-full md:w-auto flex-1">
            <label htmlFor="license-search" className="block text-sm font-medium text-gray-700 mb-1">
              Pesquisar
            </label>
            <div className="relative">
              <Input
                id="license-search"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="NÂº do pedido ou placa..."
                className="pl-10"
              />
              <span className="absolute left-3 top-2.5 text-gray-400">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </span>
            </div>
          </div>

          <div className="w-full md:w-auto">
            <label htmlFor="status-filter" className="block text-sm font-medium text-gray-700 mb-1">
              Status
            </label>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger>
                <SelectValue placeholder="Todos os status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all_status">Todos os status</SelectItem>
                <SelectItem value="pending_registration">Pedido em Cadastramento</SelectItem>
                <SelectItem value="registration_in_progress">Cadastro em Andamento</SelectItem>
                <SelectItem value="pending_documentation">Pendente DocumentaÃ§Ã£o</SelectItem>
                <SelectItem value="rejected">Reprovado</SelectItem>
                <SelectItem value="under_review">AnÃ¡lise do Ã“rgÃ£o</SelectItem>
                <SelectItem value="pending_approval">Pendente LiberaÃ§Ã£o</SelectItem>
                <SelectItem value="approved">Liberada</SelectItem>
                <SelectItem value="canceled">Cancelado</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="w-full md:w-auto">
            <label htmlFor="state-filter" className="block text-sm font-medium text-gray-700 mb-1">
              Estado
            </label>
            <Select value={stateFilter} onValueChange={setStateFilter}>
              <SelectTrigger>
                <SelectValue placeholder="Todos os estados" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all_states">Todos os estados</SelectItem>
                {brazilianStates.map((state) => (
                  <SelectItem key={state.code} value={state.code}>
                    {state.code} - {state.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="w-full md:w-auto">
            <label htmlFor="date-filter" className="block text-sm font-medium text-gray-700 mb-1">
              Data
            </label>
            <Input
              id="date-filter"
              type="date"
              value={dateFilter}
              onChange={(e) => setDateFilter(e.target.value)}
            />
          </div>
        </div>
      </div>

      {/* CabeÃ§alho com contador de licenÃ§as */}
      <div className="mb-4 flex justify-between items-center">
        <div className="text-sm text-gray-600">
          Mostrando {paginatedLicenses.length > 0 ? ((currentPage - 1) * 10 + 1) : 0}-{Math.min(currentPage * 10, sortedLicenses.length)} de {sortedLicenses.length} licenÃ§as
        </div>
      </div>

      <LicenseList 
        licenses={paginatedLicenses || []} 
        isLoading={isLoading}
        onView={handleViewLicense}
        onRefresh={refetch}
        sortColumn={sortColumn}
        sortDirection={sortDirection}
        onSort={handleSort}
      />

      {/* Controles de paginaÃ§Ã£o - VersÃ£o desktop */}
      <div className="hidden md:block mt-6">
        <ListPagination 
          currentPage={currentPage}
          totalPages={pagination.totalPages}
          totalItems={pagination.total}
          itemsPerPage={pagination.itemsPerPage}
          hasPrev={pagination.hasPrev}
          hasNext={pagination.hasNext}
          startItem={pagination.startItem}
          endItem={pagination.endItem}
          onPageChange={setCurrentPage}
          itemName="licenÃ§as"
        />
      </div>

      {/* Controles de paginaÃ§Ã£o - VersÃ£o mobile */}
      <div className="block md:hidden mt-6">
        <MobileListPagination
          currentPage={currentPage}
          totalPages={pagination.totalPages}
          totalItems={pagination.total}
          itemsPerPage={pagination.itemsPerPage}
          hasPrev={pagination.hasPrev}
          hasNext={pagination.hasNext}
          startItem={pagination.startItem}
          endItem={pagination.endItem}
          onPageChange={setCurrentPage}
          itemName="licenÃ§as"
        />
      </div>

      {selectedLicense && (
        <Dialog open={!!selectedLicense} onOpenChange={(open) => !open && setSelectedLicense(null)}>
          <DialogContent className="max-w-[1330px] max-h-[90vh] w-[98vw] overflow-y-auto p-4 sm:p-6">
            <DialogHeader className="pb-2">
              <DialogTitle className="text-xl font-bold">Detalhes da LicenÃ§a</DialogTitle>
              <DialogDescription>
                Visualize os detalhes da sua solicitaÃ§Ã£o
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-4">
              {/* Fluxo de progresso individualizado por estado */}
              {selectedLicense.states && selectedLicense.states.length > 0 && (
                <div className="mb-4 p-3 bg-gray-50 rounded-md border border-gray-200">
                  <div className="grid grid-cols-1 gap-4">
                    {selectedLicense.states.map(state => {
                      // Procura o status para este estado
                      const stateStatusEntry = selectedLicense.stateStatuses?.find(ss => ss.startsWith(`${state}:`));
                      const stateStatus = stateStatusEntry?.split(':')[1] || "pending_registration";

                      // Extrair data de validade
                      const stateValidUntil = stateStatusEntry && stateStatusEntry.split(':').length > 2 ? 
                        stateStatusEntry.split(':')[2] : undefined;

                      return (
                        <div key={state} className="border-b border-gray-200 pb-4 last:border-0 last:pb-0">
                          <h4 className="font-medium text-sm mb-2">Fluxo de Progresso da LicenÃ§a: {state}</h4>
                          <StateProgressFlow stateStatus={stateStatus} size="sm" className="py-1" />
                        </div>
                      );
                    })}
                  </div>
                </div>
              )}

              {/* Componente LicenseDetailsCard para exibiÃ§Ã£o dos detalhes */}
              <LicenseDetailsCard license={selectedLicense} />

              {/* O bloco de status especÃ­fico por estado foi removido conforme solicitado */}

              {selectedLicense.comments && (
                <div>
                  <h3 className="font-medium text-sm text-gray-500">ComentÃ¡rios</h3>
                  <p className="text-gray-600 bg-gray-50 p-3 rounded-md border border-gray-200 text-sm">
                    {selectedLicense.comments}
                  </p>
                </div>
              )}

              {/* Ãrea de Status por Estado - Exibindo apenas botÃµes de download para estados liberados */}
              {selectedLicense.states && selectedLicense.states.length > 0 && (
                <div className="mb-4">
                  <h3 className="font-medium text-sm text-gray-500 mb-2">LicenÃ§as por Estado</h3>
                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
                    {selectedLicense.states.map(state => {
                      // Verificar o status para este estado especÃ­fico
                      const stateStatusEntry = selectedLicense.stateStatuses?.find(ss => ss.startsWith(`${state}:`));
                      const stateStatus = stateStatusEntry?.split(':')[1] || "pending_registration";

                      // Extrair data de validade se existir
                      const stateValidUntil = stateStatusEntry && stateStatusEntry.split(':').length > 2 ? 
                        stateStatusEntry.split(':')[2] : undefined;

                      // Verificar se existe um arquivo especÃ­fico para este estado
                      const stateFileEntry = selectedLicense.stateFiles?.find(sf => sf.startsWith(`${state}:`));
                      const stateFileUrl = stateFileEntry ? stateFileEntry.split(':').slice(1).join(':') : undefined;

                      return (
                        <div 
                          key={state} 
                          className={`p-3 rounded-lg border ${
                            stateStatus === "approved" 
                              ? "bg-green-50 border-green-200" 
                              : "bg-gray-50 border-gray-200"
                          }`}
                        >
                          <div className="flex justify-between items-center">
                            <div>
                              <div className="flex items-center gap-2 mb-1">
                                <span className="font-medium">{state}</span>
                                <StatusBadge status={stateStatus} />
                              </div>
                              <p className="text-xs text-gray-600">
                                {stateStatus === "approved" 
                                  ? stateValidUntil 
                                    ? `LicenÃ§a liberada para download - VÃ¡lida atÃ© ${new Date(stateValidUntil).toLocaleDateString('pt-BR')}` 
                                    : "LicenÃ§a liberada para download" 
                                  : "Status em processamento"}
                              </p>
                            </div>

                            {stateStatus === "approved" && (
                              <Button 
                                variant="outline" 
                                size="sm" 
                                asChild
                                className="text-green-600 border-green-200"
                              >
                                <a 
                                  href={stateFileUrl || selectedLicense.licenseFileUrl || '#'} 
                                  target="_blank" 
                                  rel="noopener noreferrer"
                                  onClick={(e) => {
                                    if (!stateFileUrl && !selectedLicense.licenseFileUrl) {
                                      e.preventDefault();
                                      alert('Arquivo da licenÃ§a nÃ£o disponÃ­vel no momento.');
                                    }
                                  }}
                                  className={(!stateFileUrl && !selectedLicense.licenseFileUrl) ? "opacity-40 cursor-not-allowed" : ""}
                                >
                                  <Download className="h-4 w-4 mr-1" /> Download
                                </a>
                              </Button>
                            )}
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </div>
              )}

              {/* Arquivo principal da licenÃ§a (manter para compatibilidade) */}
              {selectedLicense.status === "approved" && selectedLicense.licenseFileUrl && (
                <div className="pt-4">
                  <Button asChild className="w-full">
                    <a href={selectedLicense.licenseFileUrl} target="_blank" rel="noopener noreferrer">
                      Download da LicenÃ§a Completa
                    </a>
                  </Button>
                </div>
              )}
            </div>
          </DialogContent>
        </Dialog>
      )}
    </MainLayout>
  );
}

