import React, { useState, useEffect } from "react";
import { MainLayout } from "@/components/layout/main-layout";
import { VehicleList } from "@/components/vehicles/vehicle-list";
import { VehicleForm } from "@/components/vehicles/vehicle-form";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogTitle } from "@/components/ui/dialog";
import { Plus, RefreshCw, Download } from "lucide-react";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { Vehicle } from "@shared/schema";
import { Input } from "@/components/ui/input";
import { useWebSocket } from "@/hooks/use-websocket";
import { useToast } from "@/hooks/use-toast";
import { exportToCSV } from "@/lib/csv-export";
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue
} from "@/components/ui/select";

export default function VehiclesPage() {
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [typeFilter, setTypeFilter] = useState("");
  const [statusFilter, setStatusFilter] = useState("");
  const [currentVehicle, setCurrentVehicle] = useState<Vehicle | null>(null);
  const [isRefreshing, setIsRefreshing] = useState(false);

  const queryClient = useQueryClient();
  const { toast } = useToast();
  const { isConnected } = useWebSocket();

  // Checar se existe uma placa prÃ©-preenchida no localStorage
  // (Vindo de outro componente como a tela de adicionar placas adicionais)
  useEffect(() => {
    const preFillPlate = localStorage.getItem('preFillPlate');
    if (preFillPlate) {
      // Abre automaticamente o formulÃ¡rio com a placa prÃ©-preenchida
      setIsFormOpen(true);
      // Remove do localStorage depois de usar
      localStorage.removeItem('preFillPlate');
    }
  });

  const { data: vehicles, isLoading, refetch } = useQuery<Vehicle[]>({
    queryKey: ["/api/vehicles"],
    queryFn: async () => {
      const res = await fetch("/api/vehicles", {
        credentials: "include"
      });
      if (!res.ok) {
        throw new Error("Erro ao buscar veÃ­culos");
      }
      return res.json();
    }
  });

  // FunÃ§Ã£o de atualizaÃ§Ã£o melhorada
  const handleRefresh = async () => {
    setIsRefreshing(true);
    try {
      // Invalidar cache primeiro
      await queryClient.invalidateQueries({ queryKey: ["/api/vehicles"] });

      // ForÃ§ar nova busca
      await refetch();

      toast({
        title: "Lista atualizada",
        description: "A lista de veÃ­culos foi atualizada com sucesso.",
      });
    } catch (error) {
      toast({
        title: "Erro ao atualizar",
        description: "NÃ£o foi possÃ­vel atualizar a lista de veÃ­culos.",
        variant: "destructive",
      });
    } finally {
      setIsRefreshing(false);
    }
  };

  const filteredVehicles = vehicles?.filter(vehicle => {
    const matchesSearch = !searchTerm || 
      vehicle.plate.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = !typeFilter || typeFilter === "all_types" || vehicle.type === typeFilter;
    const matchesStatus = !statusFilter || statusFilter === "all_status" || vehicle.status === statusFilter;
    return matchesSearch && matchesType && matchesStatus;
  });

  const handleAddVehicle = () => {
    setCurrentVehicle(null);
    setIsFormOpen(true);
  };

  const handleEditVehicle = (vehicle: Vehicle) => {
    setCurrentVehicle(vehicle);
    setIsFormOpen(true);
  };

  const handleFormClose = () => {
    setIsFormOpen(false);
    setCurrentVehicle(null);
  };

  const handleFormSuccess = async () => {
    try {
      // Invalidar cache e atualizar dados
      await queryClient.invalidateQueries({ queryKey: ["/api/vehicles"] });
      await refetch();

      toast({
        title: "Sucesso",
        description: "VeÃ­culo salvo com sucesso.",
      });
    } catch (error) {
      toast({
        title: "Aviso",
        description: "VeÃ­culo salvo, mas pode ser necessÃ¡rio atualizar a lista.",
        variant: "default",
      });
    } finally {
      setIsFormOpen(false);
      setCurrentVehicle(null);
    }
  };

  const handleExportCSV = () => {
    if (!vehicles || vehicles.length === 0) {
      toast({
        title: "Nenhum dado para exportar",
        description: "NÃ£o hÃ¡ veÃ­culos para exportar",
        variant: "destructive",
      });
      return;
    }

    try {
      const headers = [
        "ID",
        "Placa",
        "Tipo",
        "Marca",
        "Modelo",
        "Ano",
        "Tara (kg)",
        "Eixos",
        "Status"
      ];

      const getVehicleTypeLabel = (type: string) => {
        const types: Record<string, string> = {
          tractor_unit: "Unidade Tratora",
          semi_trailer: "Semirreboque",
          trailer: "Reboque",
          dolly: "Dolly",
          flatbed: "Prancha"
        };
        return types[type] || type;
      };

