      }));

      exportToCSV({
        filename: "veiculos",
        headers,
        data: formattedData
      });

      toast({
        title: "ExportaÃ§Ã£o concluÃ­da",
        description: `${vehicles.length} veÃ­culos exportados com sucesso`,
