                <SelectValue placeholder="Todos os status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all_status">Todos os status</SelectItem>
                <SelectItem value="active">Ativo</SelectItem>
                <SelectItem value="maintenance">Em ManutenÃ§Ã£o</SelectItem>
                <SelectItem value="inactive">Inativo</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>
      </div>

      <VehicleList 
        vehicles={filteredVehicles || []} 
        isLoading={isLoading} 
        onEdit={handleEditVehicle}
        onRefresh={handleRefresh}
      />

      <Dialog open={isFormOpen} onOpenChange={setIsFormOpen}>
        <DialogContent className="sm:max-w-[520px] p-0 max-h-[90vh] overflow-auto" hideCloseButton>
          <DialogTitle className="sr-only">
            {currentVehicle ? "Editar VeÃ­culo" : "Cadastrar VeÃ­culo"}
          </DialogTitle>
          <VehicleForm 
            vehicle={currentVehicle as any} 
            onSuccess={handleFormSuccess} 
            onCancel={handleFormClose}
          />
        </DialogContent>
      </Dialog>
    </MainLayout>
  );
}

