// FunÃ§Ã£o para formatar CNPJ
const formatCNPJ = (value: string): string => {
  // Remove tudo que nÃ£o Ã© nÃºmero
  const numbers = value.replace(/\D/g, '');

  // Aplica mÃ¡scara de CNPJ
  if (numbers.length <= 14) {
    return numbers.replace(/^(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})$/, '$1.$2.$3/$4-$5');
  }

  return numbers.slice(0, 14).replace(/^(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})$/, '$1.$2.$3/$4-$5');
};

// FunÃ§Ã£o para formatar CPF
const formatCPF = (value: string): string => {
  // Remove tudo que nÃ£o Ã© nÃºmero
  const numbers = value.replace(/\D/g, '');

  // Aplica mÃ¡scara de CPF
  if (numbers.length <= 11) {
    return numbers.replace(/^(\d{3})(\d{3})(\d{3})(\d{2})$/, '$1.$2.$3-$4');
  }

  return numbers.slice(0, 11).replace(/^(\d{3})(\d{3})(\d{3})(\d{2})$/, '$1.$2.$3-$4');
};

// FunÃ§Ã£o para extrair apenas nÃºmeros do documento (CNPJ/CPF)
const extractDocumentNumbers = (value: string): string => {
  return value.replace(/\D/g, '');
};

// FunÃ§Ã£o para validar CNPJ
const isValidCNPJ = (cnpj: string): boolean => {
  const numbers = extractDocumentNumbers(cnpj);
  return numbers.length === 14;
};

// FunÃ§Ã£o para validar CPF
const isValidCPF = (cpf: string): boolean => {
  const numbers = extractDocumentNumbers(cpf);
  return numbers.length === 11;
};

// FunÃ§Ã£o para detectar tipo de documento e formatar
const formatDocument = (value: string): string => {
  const numbers = extractDocumentNumbers(value);

  if (numbers.length === 11) {
    return formatCPF(value);
  } else if (numbers.length === 14) {
    return formatCNPJ(value);
  }

  return value; // Retorna o valor original se nÃ£o for CPF nem CNPJ vÃ¡lido
};

// FunÃ§Ã£o para validar documento (CPF ou CNPJ)
const isValidDocument = (document: string): boolean => {
  return isValidCPF(document) || isValidCNPJ(document);
};

// Interface para filial
interface Subsidiary {
  cnpj: string;
  name: string;
  tradeName?: string;
  street?: string;
  number?: string;
  complement?: string;
  zipCode?: string;
  city?: string;
  state?: string;
  documents: string[];
}

// Interface para documento
interface Document {
  type: string;
  url: string;
  filename: string;
}

