        .min(1, "CNPJ Ã© obrigatÃ³rio")
        .refine((val) => {
          const numbers = extractDocumentNumbers(val);
          return numbers.length === 14;
        }, "CNPJ deve conter 14 dÃ­gitos"),
      tradeName: z.string().optional(),
      legalResponsible: z.string().min(3, "Nome do responsÃ¡vel legal Ã© obrigatÃ³rio"),
      email: z.string().email("Email invÃ¡lido"),
      phone: z.string().min(10, "Telefone deve ter pelo menos 10 dÃ­gitos"),
      street: z.string().min(3, "Logradouro Ã© obrigatÃ³rio"),
      number: z.string().min(1, "NÃºmero Ã© obrigatÃ³rio"),
      complement: z.string().optional(),
      district: z.string().min(2, "Bairro Ã© obrigatÃ³rio"),
      zipCode: z.string().min(8, "CEP deve ter 8 dÃ­gitos"),
      city: z.string().min(2, "Cidade Ã© obrigatÃ³ria"),
      state: z.string().min(2, "Estado Ã© obrigatÃ³rio"),
      // Campos para retro-compatibilidade
      contact1Name: z.string().optional(),
      contact1Phone: z.string().optional(),
      contact2Name: z.string().optional(),
      contact2Phone: z.string().optional(),
    })),
    defaultValues: {
      personType: "pj",
      name: transporter?.name || "",
      documentNumber: transporter?.documentNumber || "",
      tradeName: transporter?.tradeName || "",
      legalResponsible: transporter?.legalResponsible || "",
      email: transporter?.email || "",
      phone: transporter?.phone || "",
      street: transporter?.street || "",
      number: transporter?.number || "",
      complement: transporter?.complement || "",
      district: transporter?.district || "",
      zipCode: transporter?.zipCode || "",
      city: transporter?.city || "",
      state: transporter?.state || "",
      // Campos para retro-compatibilidade
      contact1Name: transporter?.contact1Name || "",
      contact1Phone: transporter?.contact1Phone || "",
    }
  });

  // FormulÃ¡rio para pessoa fÃ­sica
  const pfForm = useForm<InsertTransporter>({
    resolver: zodResolver(z.object({
      personType: personTypeEnum,
      name: z.string().min(3, "O nome completo deve ter pelo menos 3 caracteres"),
      documentNumber: z.string().min(11, "CPF deve ter 11 dÃ­gitos"),
      birthDate: z.string().min(8, "Data de nascimento Ã© obrigatÃ³ria"),
      nationality: z.string().min(2, "Nacionalidade Ã© obrigatÃ³ria"),
      idNumber: z.string().min(5, "RG Ã© obrigatÃ³rio"),
      idIssuer: z.string().min(2, "Ã“rgÃ£o emissor Ã© obrigatÃ³rio"),
      idState: z.string().min(2, "UF do RG Ã© obrigatÃ³ria"),
      email: z.string().email("Email invÃ¡lido"),
      phone: z.string().min(10, "Telefone deve ter pelo menos 10 dÃ­gitos"),
      // Campos para retro-compatibilidade
      contact1Name: z.string().optional(),
      contact1Phone: z.string().optional(),
    })),
    defaultValues: {
      personType: "pf",
      name: transporter?.name || "",
      documentNumber: transporter?.documentNumber || "",
      birthDate: transporter?.birthDate || "",
      nationality: transporter?.nationality || "Brasileira",
      idNumber: transporter?.idNumber || "",
      idIssuer: transporter?.idIssuer || "",
      idState: transporter?.idState || "",
      email: transporter?.email || "",
      phone: transporter?.phone || "",
      // Campos para retro-compatibilidade
      contact1Name: transporter?.name || "",
      contact1Phone: transporter?.phone || "",
    }
  });

  // Efeito para atualizar o tipo de pessoa quando o formulÃ¡rio mudar
  useEffect(() => {
    if (personType === "pj") {
      pjForm.setValue("personType", "pj");
    } else {
      pfForm.setValue("personType", "pf");
    }
  }, [personType, pjForm, pfForm]);

  // Adicionar uma nova filial
  const addSubsidiary = () => {
    setSubsidiaries([
      ...subsidiaries,
      {
        cnpj: "",
        name: "",
