  // Remover uma filial
  const removeSubsidiary = (index: number) => {
    setSubsidiaries(subsidiaries.filter((_, i) => i !== index));
  };

  // Atualizar uma filial
  const updateSubsidiary = (index: number, field: keyof Subsidiary, value: any) => {
