    };
    setSubsidiaries(newSubsidiaries);
  };

  // Lidar com upload de arquivos
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>, fileType: string) => {
    if (e.target.files && e.target.files.length > 0) {
