          />
        </CardContent>
      </Card>

      {/* FormulÃ¡rio de Pessoa JurÃ­dica */}
      {personType === "pj" && (
        <Form {...pjForm}>
          <form onSubmit={pjForm.handleSubmit(onSubmit)} className="space-y-6">
            <Card className="w-full overflow-hidden">
