                              <Input 
                                placeholder="00.000.000/0000-00 ou 00000000000000" 
                                {...field}
                                onChange={(e) => {
                                  const value = e.target.value;
                                  // MantÃ©m o valor original no estado do formulÃ¡rio (pode ser com ou sem formataÃ§Ã£o)
                                  field.onChange(value);
                                }}
                                onBlur={(e) => {
                                  // Ao sair do campo, formatar automaticamente
                                  const value = e.target.value;
                                  const formatted = formatDocument(value);
                                  if (formatted !== value) {
                                    field.onChange(formatted);
                                  }
                                }}
                              />
                            </FormControl>
                            <Button 
                              type="button" 
                              variant="outline" 
                              size="icon" 
                              disabled={isLoadingCnpj || !field.value || !isValidCNPJ(field.value)}
                              onClick={async () => {
                                const cnpjNumbers = extractDocumentNumbers(field.value);

                                if (cnpjNumbers && cnpjNumbers.length === 14) {
                                  try {
                                    setIsLoadingCnpj(true);
                                    // Usar apenas nÃºmeros para consulta na API
                                    const response = await fetch(`/api/external/cnpj/${cnpjNumbers}`, {
                                      headers: {
                                        'Accept': 'application/json',
