import { LoadingSpinner } from "@/components/ui/loading-spinner";
import { UserSelect } from "./user-select";

interface TransporterLinkUserProps {
  transporter: Transporter;
  onSuccess?: () => void;
}

export function TransporterLinkUser({ transporter, onSuccess }: TransporterLinkUserProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedUserId, setSelectedUserId] = useState<number | null>(transporter.userId || null);

  // MutaÃ§Ã£o para vincular transportador a usuÃ¡rio
  const linkUserMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", `/api/admin/transporters/${transporter.id}/link`, {
        userId: selectedUserId
      });
      return await response.json();
    },
    onSuccess: () => {
      toast({
        title: selectedUserId ? "UsuÃ¡rio vinculado com sucesso" : "VÃ­nculo removido com sucesso",
        description: selectedUserId 
          ? "O usuÃ¡rio selecionado agora pode gerenciar este transportador"
          : "O transportador nÃ£o estÃ¡ mais vinculado a nenhum usuÃ¡rio",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/transporters"] });
      onSuccess?.();
    },
    onError: (error: Error) => {
      toast({
        title: "Erro ao vincular usuÃ¡rio",
        description: error.message,
        variant: "destructive",
      });
    }
  });

  const isPending = linkUserMutation.isPending;

  return (
    <Card className="w-full">
      <CardHeader>
        <CardTitle>VinculaÃ§Ã£o com UsuÃ¡rio</CardTitle>
        <CardDescription>
          Selecione o usuÃ¡rio que serÃ¡ responsÃ¡vel por gerenciar este transportador.
          Este usuÃ¡rio terÃ¡ acesso aos veÃ­culos e licenÃ§as deste transportador.
        </CardDescription>
      </CardHeader>
      <CardContent>
        <UserSelect
          selectedUserId={selectedUserId}
          onChange={setSelectedUserId}
          description="O usuÃ¡rio selecionado terÃ¡ acesso para gerenciar este transportador, seus veÃ­culos e licenÃ§as."
        />
      </CardContent>
      <CardFooter className="flex justify-end">
        <Button 
          onClick={() => linkUserMutation.mutate()} 
          disabled={isPending || (selectedUserId === transporter.userId)}
        >
          {isPending ? (
            <>
              <LoadingSpinner size="sm" />
              <span className="ml-2">Processando...</span>
            </>
          ) : selectedUserId ? (
            selectedUserId === transporter.userId ? "JÃ¡ vinculado" : "Vincular UsuÃ¡rio"
          ) : (
            "Remover VÃ­nculo"
          )}
        </Button>
      </CardFooter>
    </Card>
  );
}

