// Componente para campos de dimensÃ£o com tratamento inteligente para valores decimais
import { ChangeEvent, useState, useEffect, KeyboardEvent } from "react";
import { Input } from "@/components/ui/input";
import { FormControl, FormDescription, FormMessage, FormItem, FormLabel } from "@/components/ui/form";

// Interface para o componente de campo de dimensÃ£o
interface DimensionFieldProps {
  field: any;
  label: string;
  placeholder: string;
  description: string;
  fieldType?: "comprimento" | "largura" | "altura"; // Tipo de campo para comportamento especÃ­fico
  licenseType?: string; // Tipo de conjunto/licenÃ§a: flatbed (prancha) ou outros
  cargoType?: string; // Tipo de carga para regras especÃ­ficas
}

export function DimensionField({ 
  field, 
  label, 
  placeholder, 
  description, 
  fieldType = "comprimento",
  licenseType,
  cargoType
}: DimensionFieldProps) {
  const [displayValue, setDisplayValue] = useState<string>('');

  // Ao inicializar, converter o valor numÃ©rico para exibiÃ§Ã£o
  useEffect(() => {
    if (field && displayValue === '' && typeof field.value === 'number') {
      setDisplayValue(field.value.toString().replace('.', ','));
    }
  }, [field?.value, displayValue]);

  // FunÃ§Ã£o para lidar com teclas especiais como backspace
  function handleKeyDown(e: KeyboardEvent<HTMLInputElement>) {
    if (e.key === 'Backspace') {
      const input = e.currentTarget;
      const selectionStart = input.selectionStart;
      const selectionEnd = input.selectionEnd;

      // Se temos uma seleÃ§Ã£o, deixa o comportamento padrÃ£o
      if (selectionStart !== selectionEnd) return;

      // Se o cursor estÃ¡ logo apÃ³s a vÃ­rgula e estamos tentando apagar a vÃ­rgula
      if (selectionStart === displayValue.indexOf(',') + 1) {
        e.preventDefault();

        // Apagar o caractere antes da vÃ­rgula tambÃ©m (a vÃ­rgula + o nÃºmero antes dela)
        const newValue = displayValue.substring(0, selectionStart - 2) + 
                         displayValue.substring(selectionStart);

        setDisplayValue(newValue);

        // Atualizar o valor no formulÃ¡rio
        updateFormValue(newValue);

        // Posicionar o cursor corretamente
        setTimeout(() => {
          input.setSelectionRange(selectionStart - 2, selectionStart - 2);
        }, 0);
      }
    }
  }

  // FunÃ§Ã£o para tratar o evento onBlur (quando o campo perde o foco)
  function handleBlur() {
    document.body.classList.remove('keyboard-active');

    // Garante que um valor estÃ¡ definido quando o campo Ã© obrigatÃ³rio
    if (fieldType === "comprimento" && (!displayValue || displayValue === '')) {
      // Se for comprimento e o campo estiver vazio

      // Para pranchas, nÃ£o temos comprimento mÃ­nimo obrigatÃ³rio
      if (licenseType === 'flatbed') {
        // Para pranchas, podemos deixar vazio ou definir um valor inicial razoÃ¡vel
        setDisplayValue('15,00');
        updateFormValue('15,00');
      } else {
        // Para outros tipos, aplicar o valor mÃ­nimo padrÃ£o
        setDisplayValue('19,80');
        updateFormValue('19,80');
      }
      return;
    }

    // Para todos os campos de dimensÃ£o: adicionar zeros apÃ³s vÃ­rgula se necessÃ¡rio
    if (displayValue.includes(',')) {
      const parts = displayValue.split(',');
      if (parts[1] === '') {
        // Se tem vÃ­rgula mas nada depois, adiciona "00"
        setDisplayValue(parts[0] + ',00');
        updateFormValue(parts[0] + ',00');
      } else if (parts[1].length === 1) {
        // Se tem sÃ³ um dÃ­gito apÃ³s a vÃ­rgula, adiciona um zero
        setDisplayValue(parts[0] + ',' + parts[1] + '0');
        updateFormValue(parts[0] + ',' + parts[1] + '0');
      }
    } else if (displayValue && !displayValue.includes(',')) {
      // Se nÃ£o tem vÃ­rgula, adicionar ,00
      setDisplayValue(displayValue + ',00');
      updateFormValue(displayValue + ',00');
    }
  }

  // FunÃ§Ã£o para atualizar o valor no formulÃ¡rio
  function updateFormValue(value: string) {
    if (!field?.onChange) return;

    // Sanitizar para o modelo interno (sempre com ponto)
    const sanitized = value.replace(/,/g, '.').replace(/(\..*)\./g, '$1');

    // Converter para float (para a validaÃ§Ã£o em metros)
    const numericValue = sanitized === '' ? undefined : parseFloat(sanitized);

    // Remover todos os logs de validaÃ§Ã£o para evitar mensagens de console
    // NÃ£o fazer nenhuma validaÃ§Ã£o adicional aqui

    // Atualizar o campo interno com o valor numÃ©rico (em metros)
    // O backend irÃ¡ converter para centÃ­metros durante o processamento
    field.onChange(numericValue);
  }

  // FunÃ§Ã£o local para processar a entrada de dimensÃµes
  function processInput(e: ChangeEvent<HTMLInputElement>) {
    // Campo inteligente para dimensÃµes que aceita tanto ponto quanto vÃ­rgula
    let value = e.target.value;
    let cursorPos = e.target.selectionStart || 0;
    let valueChanged = false;

    // Para veÃ­culos tipo "Prancha", aceitar valores maiores (atÃ© 8 caracteres)
    // Para outros tipos, limitar a 5 caracteres
    const maxLength = licenseType === 'flatbed' ? 8 : 5;

    // Limitar a digitaÃ§Ã£o apenas a nÃºmeros, vÃ­rgula e ponto
    value = value.replace(/[^\d.,]/g, '');

    // Limitar ao nÃºmero de caracteres definido (incluindo vÃ­rgula)
    if (value.length > maxLength) {
      value = value.substring(0, maxLength);
    }

    // Assegurar que sÃ³ exista um separador decimal
    const hasComma = value.indexOf(',') !== -1;
    const hasDot = value.indexOf('.') !== -1;

    if (hasComma && hasDot) {
      // Se tiver ambos, remover o ponto
      value = value.replace(/\./g, '');
    }

    // Converter ponto para vÃ­rgula sempre (preferÃªncia formato brasileiro)
    if (hasDot) {
      value = value.replace('.', ',');
    }

    // Adicionar automaticamente vÃ­rgula baseado no tipo de campo
    if (fieldType === "comprimento") {
      // Para comprimento: adicionar vÃ­rgula apÃ³s 2 dÃ­gitos
      if (value.length === 2 && !hasComma && !value.includes(',') && /^\d\d$/.test(value)) {
        value = value + ',';
        valueChanged = true;
        cursorPos = 3;
      }
    } else {
      // Para largura e altura: adicionar vÃ­rgula apÃ³s 1 dÃ­gito
      if (value.length === 1 && !hasComma && !value.includes(',') && /^\d$/.test(value)) {
        value = value + ',';
        valueChanged = true;
        cursorPos = 2;
      }
    }

    // Limitar a 2 casas decimais durante a digitaÃ§Ã£o
    if (value.indexOf(',') !== -1) {
      const parts = value.split(',');
      if (parts[1] && parts[1].length > 2) {
        parts[1] = parts[1].substring(0, 2);
        value = parts.join(',');
      }
    }

    // Atualizar o valor de exibiÃ§Ã£o
    setDisplayValue(value);

    // Posicionar o cursor corretamente se o valor mudou
    if (valueChanged) {
      setTimeout(() => {
        e.target.setSelectionRange(cursorPos, cursorPos);
      }, 0);
    }

    // Atualizar o valor no formulÃ¡rio
    updateFormValue(value);
  }

  // Gerar ID Ãºnico para o campo
  const fieldId = `${fieldType}_input_${field?.name || 'unknown'}`;

  // Verificar se o campo estÃ¡ vazio para mostrar alerta
  const isEmpty = !field || field.value === undefined || field.value === null || field.value === '';

  return (
    <FormItem>
      <FormLabel htmlFor={fieldId} className="text-base font-medium flex items-center">
        {label}
        {isEmpty && (
          <span className="ml-2 text-xs font-normal px-2 py-0.5 rounded-full bg-amber-100 text-amber-800">
            ObrigatÃ³rio
          </span>
        )}
      </FormLabel>
      <FormControl>
        <Input 
          id={fieldId}
          type="text" 
          inputMode="decimal"
          placeholder={placeholder}
          value={displayValue}
          className={`mobile-input h-10 ${isEmpty ? 'border-amber-500 focus:ring-amber-500' : ''}`}
          onFocus={(e) => {
            document.body.classList.add('keyboard-active');
            window.scrollTo(0, 0);
            setTimeout(() => {
              e.target.scrollIntoView({ behavior: 'smooth', block: 'center' });
            }, 300);
          }}
          onBlur={handleBlur}
          onChange={processInput}
          onKeyDown={handleKeyDown}
        />
      </FormControl>
      {isEmpty && (
        <div className="mt-1 text-sm text-amber-600 font-medium">
          Este campo Ã© obrigatÃ³rio. Por favor, preencha um valor.
        </div>
      )}
      <FormDescription className="text-xs text-muted-foreground mt-1">
        {description}
      </FormDescription>
      {/* NÃ£o exibimos mensagens de validaÃ§Ã£o para pranchas em nenhum campo de dimensÃ£o */}
      {licenseType === "flatbed" ? (
        <div className="hidden"><FormMessage /></div>
      ) : (
        <FormMessage />
      )}
    </FormItem>
  );
}

