              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Componente de histÃ³rico de status */}
      <div className="mt-6">
        <StatusHistoryNew licenseId={license.id} states={license.states || []} />
      </div>
    </div>
  );
}

